SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE gpt_alterar_data_procedimento ( nr_seq_cpoe_proced_p cpoe_procedimento.nr_sequencia%type, dt_nova_data_p timestamp, nm_usuario_p usuario.nm_usuario%type, cd_estabelecimento_p estabelecimento.cd_estabelecimento%type, cd_perfil_p perfil.cd_perfil%type) AS $body$
DECLARE


nr_atendimento_w		cpoe_procedimento.nr_atendimento%type;
cd_intervalo_w			cpoe_procedimento.cd_intervalo%type;
ds_horarios_w			cpoe_procedimento.ds_horarios%type;
ds_horarios2_w			cpoe_procedimento.ds_horarios%type;
nr_seq_proc_interno_w	cpoe_procedimento.nr_seq_proc_interno%type;
nr_prescricao_w			prescr_procedimento.nr_prescricao%type;	
ie_acm_w				prescr_procedimento.ie_acm%type;
ie_sn_w					prescr_procedimento.ie_se_necessario%type;
ie_operacao_w			intervalo_prescricao.ie_operacao%type;
nr_intervalo_w			bigint;
dt_nova_data_w			timestamp;


BEGIN
if (nr_seq_cpoe_proced_p IS NOT NULL AND nr_seq_cpoe_proced_p::text <> '' AND dt_nova_data_p IS NOT NULL AND dt_nova_data_p::text <> '') then
	if (dt_nova_data_p < clock_timestamp()) then
		CALL wheb_mensagem_pck.exibir_mensagem_abort(1046176); --A data de inicio nao pode ser anterior a data atual.
	end if;
	
	dt_nova_data_w	:= dt_nova_data_p;
	
	select	max(a.nr_atendimento),
			max(a.cd_intervalo),
			max(a.nr_seq_proc_interno),
			max(a.ie_acm),
			max(a.ie_se_necessario)
	into STRICT	nr_atendimento_w,
			cd_intervalo_w,
			nr_seq_proc_interno_w,
			ie_acm_w,
			ie_sn_w
	from	cpoe_procedimento	a
	where	a.nr_sequencia	= nr_seq_cpoe_proced_p;

	select	max(ie_operacao)
	into STRICT	ie_operacao_w
	from	intervalo_prescricao	a
	where	a.cd_intervalo	= cd_intervalo_w;
	
	SELECT * FROM cpoe_calcular_horario_prescr(
		nr_atendimento_w, cd_intervalo_w, null, dt_nova_data_w, null, null, nr_intervalo_w, ds_horarios_w, ds_horarios2_w, nm_usuario_p, cd_estabelecimento_p, cd_perfil_p, null, null, null, null, nr_seq_proc_interno_w, null) INTO STRICT nr_intervalo_w, ds_horarios_w, ds_horarios2_w;
		
		ds_horarios_w := ds_horarios_w || ds_horarios2_w;
	
	if (ie_acm_w = 'S' or ie_sn_w = 'S') then
		dt_nova_data_w	:= trunc(dt_nova_data_w, 'dd');
		ds_horarios_w := replace(ds_horarios_w, substr(ds_horarios_w, 1, 5), '00:00');
	end if;
	
	update	cpoe_procedimento	a
	set		a.ds_horarios		= trim(both ds_horarios_w),
			a.dt_inicio			= dt_nova_data_w,
			a.hr_prim_horario	= to_char(dt_nova_data_w,'hh24:mi')
	where	a.nr_sequencia	= nr_seq_cpoe_proced_p;

	commit;

	select	max(a.nr_prescricao)
	into STRICT	nr_prescricao_w
	from	prescr_procedimento	a
	where	a.nr_seq_proc_cpoe	= nr_seq_cpoe_proced_p;
			
	CALL cpoe_atualizar_rep(	
						nr_prescricao_w,
						nr_seq_cpoe_proced_p,
						'P',
						nm_usuario_p,
						cd_perfil_p,
						cd_estabelecimento_p);

end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
