SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE gpt_enf_liberar_item ( nr_seq_mat_cpoe_p bigint, nr_atendimento_p bigint, ie_tipo_item_p text, cd_perfil_p bigint, cd_estabelecimento_p bigint, nm_usuario_p text) AS $body$
DECLARE


cd_convenio_w			atend_categoria_convenio.cd_convenio%type;
cd_medico_w				prescr_medica.cd_medico%type;
nr_prescricao_w			prescr_medica.nr_prescricao%type;
nr_sequencia_w			prescr_material.nr_sequencia%type;
cd_material_w			prescr_material.cd_material%type;

c01 CURSOR FOR
SELECT	a.nr_prescricao,
		a.nr_sequencia
from	prescr_material a
where	a.nr_seq_mat_cpoe = nr_seq_mat_cpoe_p
and		ie_tipo_item_p = 'M'

union

SELECT	a.nr_prescricao,
		a.nr_sequencia_solucao nr_sequencia
from	prescr_material a
where	a.ie_agrupador = 4
and		a.nr_seq_mat_cpoe = nr_seq_mat_cpoe_p
and		ie_tipo_item_p = 'SOL';
	
c02 CURSOR FOR
SELECT	cd_material
from	prescr_material
where	nr_prescricao = nr_prescricao_w
and		nr_sequencia_solucao = nr_sequencia_w
and		ie_agrupador = 4;
	

BEGIN

select 	max(cd_convenio)
into STRICT	cd_convenio_w
from 	atend_categoria_convenio
where	nr_atendimento = nr_atendimento_p;

open c01;
loop
fetch c01 into
	nr_prescricao_w,
	nr_sequencia_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
	begin

	CALL enf_liberar_item(nr_prescricao_w, nr_sequencia_w, nr_atendimento_p, cd_perfil_p, nm_usuario_p, ie_tipo_item_p, cd_estabelecimento_p);

	select	max(cd_medico)
	into STRICT	cd_medico_w
	from 	prescr_medica
	where	nr_prescricao = nr_prescricao_w;

	if (ie_tipo_item_p = 'SOL') then
	
		open c02;
		loop
		fetch c02 into
			cd_material_w;
		EXIT WHEN NOT FOUND; /* apply on c02 */
			begin	
			CALL evento_metas_medicamento(cd_convenio_w, cd_material_w, cd_medico_w, cd_medico_w, nr_atendimento_p, cd_estabelecimento_p, cd_perfil_p, obter_setor_ativo, '', null);	
			end;
		end loop;
		close c02;
	
	elsif (ie_tipo_item_p = 'M') then
	
		select	max(cd_material)
		into STRICT	cd_material_w
		from	prescr_material
		where	nr_prescricao = nr_prescricao_w
		and		nr_sequencia = nr_sequencia_w;

		CALL evento_metas_medicamento(cd_convenio_w, cd_material_w, cd_medico_w, cd_medico_w, nr_atendimento_p, cd_estabelecimento_p, cd_perfil_p, obter_setor_ativo, '', null);	

	end if;

	end;
end loop;
close c01;

update	cpoe_material
set		nm_usuario_lib_enf = nm_usuario_p,
		dt_liberacao_enf = clock_timestamp()
where	nr_sequencia = nr_seq_mat_cpoe_p;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
