CREATE OR REPLACE PROCEDURE gpt_insert_hist_reval_doc (ds_itens_p text, nr_atendimento_p bigint, cd_pessoa_fisica_p text, ds_observacao_p text) AS $body$
DECLARE


ie_tipo_usuario_w		varchar(1);
nr_seq_reval_events_w	cpoe_revalidation_events.nr_sequencia%type;

c01 CURSOR FOR
SELECT trim(both substr(cd_registro, 1, position(','  cd_registro) - 1)) ie_tipo_item,
   (substr(cd_registro, position(',' in cd_registro) + 1, length(cd_registro)))::numeric  nr_seq_cpoe
from table(lista_pck.obter_lista_char(ds_itens_p, ';'))
where   cd_registro like '%,%';

BEGIN

if ((ds_itens_p IS NOT NULL AND ds_itens_p::text <> '') and ((nr_atendimento_p IS NOT NULL AND nr_atendimento_p::text <> '') or (cd_pessoa_fisica_p IS NOT NULL AND cd_pessoa_fisica_p::text <> ''))) then

	for item in c01
	loop
		ie_tipo_usuario_w := Obter_param_Usuario(252, 1, obter_perfil_ativo, obter_usuario_ativo, obter_estabelecimento_ativo, ie_tipo_usuario_w);

		nr_seq_reval_events_w := cpoe_obter_seq_reval_events(item.nr_seq_cpoe, gpt_convert_tipo_item_cpoe(item.ie_tipo_item));

		insert into gpt_hist_reval_events_doc(
			nr_sequencia,
			nr_seq_reval_events,
			nr_seq_cpoe,
			nr_atendimento,
			nm_usuario_nrec,
			nm_usuario,
			ie_tipo_usuario,
			dt_atualizacao_nrec,
			dt_atualizacao,
			ds_observacao,
			cd_pessoa_fisica,
			cd_perfil_ativo,
			cd_estabelecimento)
			values (
			nextval('gpt_hist_reval_events_doc_seq'),
			nr_seq_reval_events_w,
			item.nr_seq_cpoe,
			nr_atendimento_p,
			obter_usuario_ativo,
			obter_usuario_ativo,
			ie_tipo_usuario_w,
			clock_timestamp(),
			clock_timestamp(),
			ds_observacao_p,
			cd_pessoa_fisica_p,
			obter_perfil_ativo,
			obter_estabelecimento_ativo);
	end loop;
	
	commit;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
