CREATE OR REPLACE PROCEDURE gpt_reverte_analise_prescr ( nr_atendimento_p bigint, nm_usuario_p text, cd_estabelecimento_p bigint) AS $body$
BEGIN

if (nr_atendimento_p IS NOT NULL AND nr_atendimento_p::text <> '') and (nm_usuario_p IS NOT NULL AND nm_usuario_p::text <> '')  then
	
	/*	Reverter a analise das prescricoes que nao foram liberadas pela farmacia
		Revert the analysis of prescriptions that were not released by the pharmacy	*/
	update	prescr_medica a
	set		a.dt_inicio_analise_farm  = NULL,
			a.nm_usuario_analise_farm  = NULL
	where	(a.dt_inicio_analise_farm IS NOT NULL AND a.dt_inicio_analise_farm::text <> '')
	and		(a.dt_liberacao IS NOT NULL AND a.dt_liberacao::text <> '')
	and		coalesce(a.dt_liberacao_farmacia::text, '') = ''
	and		a.cd_funcao_origem not in (924,950)
	and		a.dt_prescricao > clock_timestamp() - interval '3 days'
	and		a.nr_atendimento = nr_atendimento_p
	and		a.nm_usuario_analise_farm = nm_usuario_p;

	commit;

end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
