CREATE OR REPLACE PROCEDURE gpt_revisar_prescricao_plano ( nr_atendimento_p bigint, cd_pessoa_fisica_p text, nm_usuario_p text) AS $body$
DECLARE

			
nr_prescr_revisao_w		prescr_medica.nr_prescricao%type;

c01 CURSOR FOR
    SELECT  nr_prescricao
    from    table(gpt_utils_pck.get_rev_prescr_plan( nr_atendimento_p,
                                                     cd_pessoa_fisica_p,
                                                     'S'));

BEGIN

open c01;
loop
fetch c01 into
	nr_prescr_revisao_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
	begin

	update	prescr_medica
	set		dt_revisao	= clock_timestamp(),
			nm_usuario_revisao	= nm_usuario_p
	where	nr_prescricao		= nr_prescr_revisao_w;

	end;
end loop;
close c01;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
