SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE gpw_duplicar_cad_avaliacao ( nr_sequencia_p bigint, nm_usuario_p text) AS $body$
DECLARE



nr_seq_proj_avaliacao_w		bigint;
nr_seq_modelo_w			bigint;
nr_seq_proj_aval_opcao_w	bigint;
nr_seq_aval_ant_w		bigint;

c01 CURSOR FOR
	SELECT	a.cd_empresa,
		a.ds_modelo,
		a.ie_situacao,
		--a.ie_consultor_projeto,
		a.ie_destino,
		a.ie_tipo_setor
	from	proj_modelo_avaliacao a
	where	a.nr_sequencia	= nr_sequencia_p;

c02 CURSOR FOR
	SELECT	a.nr_sequencia,
		a.cd_empresa,
		a.ie_tipo_avaliacao,
		a.nr_seq_apres,
		a.ds_quesito,
		a.ie_tipo_quesito,
		a.qt_peso,
		a.ie_situacao,
		a.nr_seq_modelo
	from	proj_avaliacao a,
		proj_modelo_avaliacao b
	where	a.nr_seq_modelo = b.nr_sequencia
	and	b.nr_sequencia	= nr_sequencia_p;

c03 CURSOR FOR
	SELECT	a.nr_seq_avaliacao,
		a.nr_seq_apres,
		a.ds_opcao,
		a.qt_peso,
		a.ie_situacao
	from	proj_aval_opcao a
	where	a.nr_seq_avaliacao	= nr_seq_aval_ant_w;




vet01 c01%rowtype;
vet02 c02%rowtype;
vet03 c03%rowtype;


BEGIN

open c01;
loop
fetch c01 into vet01;
EXIT WHEN NOT FOUND; /* apply on c01 */


select	nextval('proj_modelo_avaliacao_seq')
into STRICT	nr_seq_modelo_w
;

insert into proj_modelo_avaliacao( 	nr_sequencia,
					cd_empresa,
					dt_atualizacao,
					nm_usuario,
					dt_atualizacao_nrec,
					nm_usuario_nrec,
					ds_modelo,
					ie_situacao,
					--ie_consultor_projeto,
					ie_destino,
					ie_tipo_setor)
				values (	nr_seq_modelo_w,
					vet01.cd_empresa,
					clock_timestamp(),
					nm_usuario_p,
					clock_timestamp(),
					nm_usuario_p,
					vet01.ds_modelo,
					vet01.ie_situacao,
					--vet01.ie_consultor_projeto,
					vet01.ie_destino,
					vet01.ie_tipo_setor);

end loop;
close c01;

open c02;
loop
fetch c02 into vet02;
EXIT WHEN NOT FOUND; /* apply on c02 */

select	nextval('proj_avaliacao_seq')
into STRICT	nr_seq_proj_avaliacao_w
;

select	vet02.nr_sequencia
into STRICT	nr_seq_aval_ant_w
;

insert into proj_avaliacao(	nr_sequencia,
				cd_empresa,
				dt_atualizacao,
				nm_usuario,
				ie_tipo_avaliacao,
				nr_seq_apres,
				ds_quesito,
				ie_tipo_quesito,
				qt_peso,
				ie_situacao,
				dt_atualizacao_nrec,
				nm_usuario_nrec,
				nr_seq_modelo)
			values (nr_seq_proj_avaliacao_w,
				vet02.cd_empresa,
				clock_timestamp(),
				nm_usuario_p,
				vet02.ie_tipo_avaliacao,
				vet02.nr_seq_apres,
				vet02.ds_quesito,
				vet02.ie_tipo_quesito,
				vet02.qt_peso,
				vet02.ie_situacao,
				clock_timestamp(),
				nm_usuario_p,
				nr_seq_modelo_w);


	open c03;
	loop
	fetch c03 into vet03;
	EXIT WHEN NOT FOUND; /* apply on c03 */

	select 	nextval('proj_aval_opcao_seq')
	into STRICT	nr_seq_proj_aval_opcao_w
	;

	insert into proj_aval_opcao(	nr_sequencia,
					nr_seq_avaliacao,
					dt_atualizacao,
					nm_usuario,
					nr_seq_apres,
					ds_opcao,
					qt_peso,
					ie_situacao,
					dt_atualizacao_nrec,
					nm_usuario_nrec)
				values (	nr_seq_proj_aval_opcao_w,
					nr_seq_proj_avaliacao_w,
					clock_timestamp(),
					nm_usuario_p,
					vet03.nr_seq_apres,
					vet03.ds_opcao,
					vet03.qt_peso,
					vet03.ie_situacao,
					clock_timestamp(),
					nm_usuario_p);

	end loop;
	close c03;
end loop;
close c02;

commit;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE gpw_duplicar_cad_avaliacao ( nr_sequencia_p bigint, nm_usuario_p text) FROM PUBLIC;

