SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE gpw_gerar_pendencia_ordem_serv ( nr_sequencia_p bigint, nm_usuario_p text) AS $body$
DECLARE


ds_titulo_w			varchar(80);
ds_pendencia_w			varchar(4000);
qt_existe_w			bigint;
nr_seq_pendencia_w		bigint;
nr_seq_classif_w			bigint;
nr_seq_mod_impl_w		bigint;
cd_funcao_w			integer;
nr_seq_indicador_w			bigint;
cd_relatorio_w			integer;
cd_classif_relat_w			varchar(4);
nm_tabela_w			varchar(50);

C01 CURSOR FOR
	SELECT	a.nr_seq_classif,
		a.nr_seq_mod_impl,
		a.cd_funcao,
		a.nr_seq_indicador,
		a.cd_relatorio,
		a.cd_classif_relat,
		a.nm_tabela
	from (SELECT	gpw_obter_seq_classif_pend('F') nr_seq_classif,
			a.nr_seq_mod_impl,
			a.cd_funcao,
			null nr_seq_indicador,
			null cd_relatorio,
			null cd_classif_relat,
			null nm_tabela
		from	funcao a
		where	a.ie_situacao <> 'I'
		and	a.nr_seq_ordem_serv = nr_sequencia_p
		
union all

		select	gpw_obter_seq_classif_pend('I') nr_seq_classif,
			null nr_seq_mod_impl,
			null cd_funcao,
			a.nr_sequencia nr_seq_indicador,
			null cd_relatorio,
			null cd_classif_relat,
			null nm_tabela
		from	indicador_gestao a
		where	a.ie_situacao = 'A'
		and	a.nr_seq_ordem_serv = nr_sequencia_p
		
union all

		select	gpw_obter_seq_classif_pend('P') nr_seq_classif,
			null nr_seq_mod_impl,
			null cd_funcao,
			null nr_seq_indicador,
			null cd_relatorio,
			null cd_classif_relat,
			null nm_tabela
		from	prontuario_item a
		where	a.ie_situacao = 'A'
		and	a.nr_seq_ordem_serv = nr_sequencia_p
		
union all

		select	gpw_obter_seq_classif_pend('P') nr_seq_classif,
			null nr_seq_mod_impl,
			null cd_funcao,
			null nr_seq_indicador,
			null cd_relatorio,
			null cd_classif_relat,
			null nm_tabela
		from	pepo_item a
		where	a.nr_seq_ordem_serv = nr_sequencia_p
		
union all

		select	gpw_obter_seq_classif_pend('P') nr_seq_classif,
			null nr_seq_mod_impl,
			null cd_funcao,
			null nr_seq_indicador,
			null cd_relatorio,
			null cd_classif_relat,
			null nm_tabela
		from	oftalmologia_item a
		where	a.nr_seq_ordem_serv = nr_sequencia_p
		
union all

		select	gpw_obter_seq_classif_pend('R') nr_seq_classif,
			null nr_seq_mod_impl,
			null cd_funcao,
			null nr_seq_indicador,
			a.cd_relatorio,
			a.cd_classif_relat,
			null nm_tabela
		from	relatorio a
		where	substr(a.cd_classif_relat,1,1) = 'W'
		and	a.nr_seq_ordem_serv = nr_sequencia_p
		
union all

		select	gpw_obter_seq_classif_pend('T') nr_seq_classif,
			a.nr_seq_mod_impl,
			null cd_funcao,
			null nr_seq_indicador,
			null cd_relatorio,
			null cd_classif_relat,
			a.nm_tabela
		from	tabela_sistema a
		where	a.ie_situacao <> 'I'
		and	a.nr_seq_ordem_serv = nr_sequencia_p
		
union all

		select	gpw_obter_seq_classif_pend('A') nr_seq_classif,
			null nr_seq_mod_impl,
			null cd_funcao,
			null nr_seq_indicador,
			null cd_relatorio,
			null cd_classif_relat,
			null nm_tabela
		from	man_ordem_ativ_prev a
		where	a.nr_seq_ordem_serv = nr_sequencia_p
		and	a.nr_seq_ativ_exec in (2,5)  LIMIT 1) a
	where	(a.nr_seq_classif IS NOT NULL AND a.nr_seq_classif::text <> '')
	order by 1;


BEGIN

if (coalesce(nr_sequencia_p,0) <> 0) and (coalesce(nm_usuario_p,'X') <> 'X') then
	begin
	select	nextval('gpw_pendencia_seq')
	into STRICT	nr_seq_pendencia_w
	;

	ds_titulo_w	:= substr('Pendência gerada a partir da OS ' || nr_sequencia_p,1,80);
	ds_pendencia_w	:= substr('Esta pendência foi gerada a partir da ordem de serviço nº ' || nr_sequencia_p || '.',1,4000);

	insert into gpw_pendencia(
			nr_sequencia,
			ds_titulo,
			dt_atualizacao,
			nm_usuario,
			dt_atualizacao_nrec,
			nm_usuario_nrec,
			ds_pendencia,
			ie_status,
			nr_seq_ordem_serv)
		values (	nr_seq_pendencia_w,
			ds_titulo_w,
			clock_timestamp(),
			nm_usuario_p,
			clock_timestamp(),
			nm_usuario_p,
			ds_pendencia_w,
			'P',
			nr_sequencia_p);

	open C01;
	loop
	fetch C01 into
		nr_seq_classif_w,
		nr_seq_mod_impl_w,
		cd_funcao_w,
		nr_seq_indicador_w,
		cd_relatorio_w,
		cd_classif_relat_w,
		nm_tabela_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
		begin
		insert into gpw_pendencia_classif(
					nr_sequencia,
					nr_seq_pendencia,
					nr_seq_classif,
					dt_atualizacao,
					nm_usuario,
					dt_atualizacao_nrec,
					nm_usuario_nrec,
					nr_seq_indicador,
					cd_funcao,
					nm_tabela,
					cd_relatorio,
					cd_classif_relat,
					nr_seq_mod_impl)
				values (	nextval('gpw_pendencia_classif_seq'),
					nr_seq_pendencia_w,
					nr_seq_classif_w,
					clock_timestamp(),
					nm_usuario_p,
					clock_timestamp(),
					nm_usuario_p,
					nr_seq_indicador_w,
					cd_funcao_w,
					nm_tabela_w,
					cd_relatorio_w,
					cd_classif_relat_w,
					nr_seq_mod_impl_w);
		end;
	end loop;
	close C01;

	--commit;
	end;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
