CREATE OR REPLACE PROCEDURE gp_retirar_armazen_prot (nr_seq_protocolo_p text) AS $body$
DECLARE

 
nr_atendimento_w		varchar(255);

C01 CURSOR FOR	 
	SELECT	d.nr_atendimento 
	from	atendimento_paciente d, 
		conta_paciente b, 
		protocolo_convenio c 
	where	d.nr_atendimento  	= b.nr_atendimento 
	and	b.nr_seq_protocolo 	= c.nr_seq_protocolo 
	and	c.nr_seq_protocolo 	= nr_seq_protocolo_p 
	order by d.nr_atendimento;


BEGIN 
 
open C01;
loop 
fetch C01 into 	nr_atendimento_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
 
	begin 
	update	same_prontuario 
	set	nr_seq_caixa  = NULL, 
	cd_maco  = NULL 
	where	nr_atendimento = nr_atendimento_w;		
	 
	end;
end loop;
close C01;
 
commit;
 
end;
