CREATE OR REPLACE PROCEDURE gqa_excluir_protocolo ( nr_seq_protocolo_p gqa_protocolo_pac.nr_sequencia%type ) AS $body$
DECLARE


devedeletar                bigint;


BEGIN
  begin
      select 1 into STRICT devedeletar from gqa_protocolo_pac where nr_sequencia = nr_seq_protocolo_p and coalesce(dt_liberacao::text, '') = '';
  exception when no_data_found then
      devedeletar := 0;
  end;

  if (devedeletar = 1) then
    delete from gqa_protocolo_etapa_pac where nr_seq_prot_pac = nr_seq_protocolo_p;
    delete from gqa_protocolo_pac where nr_sequencia = nr_seq_protocolo_p;
  end if;

end;
$body$
