CREATE OR REPLACE PROCEDURE gqa_finalizar_protocolo ( nr_seq_protocolo_p gqa_protocolo_pac.nr_sequencia%type, nr_seq_motivo_p gqa_protocolo_pac.nr_seq_motivo%type, ds_justificativa_p gqa_protocolo_pac.ds_justificativa%type ) AS $body$
DECLARE


cd_profissional_w         pessoa_fisica.cd_pessoa_fisica%type;
nm_usuario_w              gqa_protocolo_pac.nm_usuario%type;
devefinalizar             smallint;

  etapa RECORD;

BEGIN
  select obter_usuario_ativo into STRICT nm_usuario_w;

  select (obter_pf_usuario(nm_usuario_w, 'C'))::numeric  into STRICT cd_profissional_w;

  if ((nr_seq_protocolo_p IS NOT NULL AND nr_seq_protocolo_p::text <> '')
    and (nm_usuario_w IS NOT NULL AND nm_usuario_w::text <> '')
    and (cd_profissional_w IS NOT NULL AND cd_profissional_w::text <> '')) then

        select count(*) into STRICT devefinalizar from gqa_protocolo_pac where nr_sequencia = nr_seq_protocolo_p and (dt_liberacao IS NOT NULL AND dt_liberacao::text <> '');

        if (devefinalizar = 1) then
          for etapa in (SELECT nr_sequencia from gqa_protocolo_etapa_pac where nr_seq_prot_pac = nr_seq_protocolo_p) loop
            CALL gqa_liberar_campo_etapa(etapa.nr_sequencia, 'S');
          end loop;

          update gqa_protocolo_pac set 
            dt_termino = clock_timestamp(), 
            nm_usuario_termino = nm_usuario_w, 
            ds_justificativa = ds_justificativa_p, 
            nr_seq_motivo = nr_seq_motivo_p 
          where nr_sequencia = nr_seq_protocolo_p;
        end if;
  end if;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
