SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE gqa_gerar_programa ( nr_seq_programa_p bigint, nr_atendimento_p bigint, cd_pessoa_fisica_p text, nm_usuario_p text, NR_SEQ_PEND_PAC_ACAO_p bigint ) AS $body$
DECLARE


nr_Seq_atend_pro_w		bigint;
ie_nivel_atencao_w		atendimento_paciente.ie_nivel_atencao%type;
nr_seq_problema_w		lista_problema_pac.nr_sequencia%type;
nr_seq_soap_w			lista_problema_pac.nr_seq_soap%type;
									

BEGIN


if (nr_seq_programa_p IS NOT NULL AND nr_seq_programa_p::text <> '') then

	select  nextval('atend_programa_saude_seq')
	into STRICT	nr_Seq_atend_pro_w
	;
	
	Select  max(ie_nivel_atencao)
	into STRICT	ie_nivel_atencao_w
	from	atendimento_paciente
	where   nr_atendimento = nr_atendimento_p;

	SELECT MAX(NR_SEQ_PROBLEMA)
	INTO STRICT   nr_seq_problema_w	
	FROM   gqa_pend_pac_acao A,
		   GQA_PENDENCIA_PAC B,	   					
		   diagnostico_doenca c,
		   LISTA_PROBLEMA_PAC_ITEM d
	WHERE  A.nr_sequencia = NR_SEQ_PEND_PAC_ACAO_p
	AND    B.NR_SEQUENCIA = A.NR_SEQ_PEND_PAC
	AND	   c.nr_seq_interno = b.nr_seq_diag_doenca
	AND	   d.nr_seq_registro = c.nr_seq_interno
	AND	   d.ie_tipo_registro = 'DM';
	
	if (coalesce(nr_seq_problema_w,0) > 0) then
	
		Select  max(nr_seq_soap)
		into STRICT	nr_seq_soap_w
		from 	lista_problema_pac
		where 	nr_sequencia = nr_seq_problema_w;
	
	end if;
	

	insert into atend_programa_saude( nr_sequencia,
									   dt_Atualizacao,
									   dt_Atualizacao_nrec,
									   nm_usuario,
									   nm_usuario_nrec,
									   NR_SEQ_PROGRAMA_SAUDE,
									   NR_ATENDIMENTO,
									   CD_PESSOA_FISICA,
									   dt_liberacao,
									   nr_seq_pend_pac_acao,
									   ie_situacao,
									   IE_NIVEL_ATENCAO,
									   nr_seq_soap)
								values (nr_Seq_atend_pro_w,
										clock_timestamp(),
										clock_timestamp(),
										nm_usuario_p,
										nm_usuario_p,
										nr_seq_programa_p,
										nr_atendimento_p,
										cd_pessoa_fisica_p,
										clock_timestamp(),
										NR_SEQ_PEND_PAC_ACAO_p,
										'A',
										ie_nivel_atencao_w,
										nr_seq_soap_w);
				
	commit;	
	
	
	if (coalesce(nr_seq_problema_w,0) > 0) then
	
		CALL vincular_problema_prog_saude(nr_seq_problema_w,nr_Seq_atend_pro_w,cd_pessoa_fisica_p);
		
	end if;
	
	CALL gerar_pend_inter_soap_pc(nr_Seq_atend_pro_w,'P');


end if;


if (coalesce(wheb_usuario_pck.get_ie_commit, 'S') = 'S') then commit; end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE gqa_gerar_programa ( nr_seq_programa_p bigint, nr_atendimento_p bigint, cd_pessoa_fisica_p text, nm_usuario_p text, NR_SEQ_PEND_PAC_ACAO_p bigint ) FROM PUBLIC;

