SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE gqa_gerar_protocolo_assist ( nr_atendimento_p bigint, ie_evento_p text, nm_usuario_p text, cd_protocolo_p bigint default 0, nr_seq_protocolo_p bigint default 0, cd_classe_material_p bigint default 0, nr_seq_familia_p bigint default 0, cd_material_p bigint default 0, nr_prescricao_p bigint default null) AS $body$
DECLARE



					
nr_seq_pend_regra_w	    bigint;	
nr_atendimento_w	    bigint;
cd_pessoa_fisica_w	    varchar(10);
cd_doenca_cid_w		    varchar(10);	
qt_idade_w		        bigint;
cd_setor_atendimento_w	bigint;		
cd_classif_setor_w	    varchar(10);		
qt_idade_dias_w		    integer;
qt_peso_gramas_w		double precision;
qt_reg_w				numeric(38);
qt_reg_sae_w			numeric(38);
ie_insert_rule_w		varchar(80) := 'Y';	
					
C01 CURSOR FOR
	SELECT	a.nr_sequencia
	from	gqa_pendencia_regra a,
		gqa_pendencia b
	where	b.nr_sequencia	= a.nr_seq_pendencia
	and	b.ie_tipo_pendencia	= 6
	and	b.ie_situacao 		= 'A'
	and	a.ie_situacao		= 'A'
	and	obter_se_gqa_regra_liberada(a.nr_sequencia) = 'S'
	and	a.ie_evento			= ie_evento_p
	and	qt_idade_w between coalesce(a.qt_idade_min,0) and coalesce(a.qt_idade_max,999)
	and     qt_idade_dias_w between coalesce(a.qt_dias_min,0) and coalesce(a.qt_dias_max,99999)
	and	qt_peso_gramas_w between coalesce(a.qt_peso_gramas_min,0) and coalesce(a.qt_peso_gramas_max,999999999)
	and	coalesce(a.cd_setor_atendimento,coalesce(cd_setor_atendimento_w,0))	= coalesce(cd_setor_atendimento_w,0)
	and	coalesce(cd_classif_setor,coalesce(cd_classif_setor_w,0))	= coalesce(cd_classif_setor_w,0);
	
c02 CURSOR FOR
	SELECT a.nr_sequencia
	from   gqa_pendencia_regra a,
		   gqa_pendencia b
	where  b.nr_sequencia = a.nr_seq_pendencia
	and	   b.ie_tipo_pendencia	= 6
	and	   b.ie_situacao = 'A'
	and	   a.ie_situacao = 'A'
	and	   obter_se_gqa_regra_liberada(a.nr_sequencia) = 'S'
	and	   a.ie_evento = ie_evento_p
	and	   qt_idade_w between coalesce(a.qt_idade_min,0) and coalesce(a.qt_idade_max,999)
	and    qt_idade_dias_w between coalesce(a.qt_dias_min,0) and coalesce(a.qt_dias_max,99999)
	and	   qt_peso_gramas_w between coalesce(a.qt_peso_gramas_min,0) and coalesce(a.qt_peso_gramas_max,999999999)
	and	   coalesce(a.cd_setor_atendimento,coalesce(cd_setor_atendimento_w,0)) = coalesce(cd_setor_atendimento_w,0)
	and	   coalesce(cd_classif_setor,coalesce(cd_classif_setor_w,0)) = coalesce(cd_classif_setor_w,0)
	and    coalesce(cd_protocolo, coalesce(cd_protocolo_p,0)) = coalesce(cd_protocolo_p,0)
	and    coalesce(nr_seq_protocolo, coalesce(nr_seq_protocolo_p,0)) = coalesce(nr_seq_protocolo_p,0)
	and    coalesce(cd_classe_material,cd_classe_material_p) = cd_classe_material_p
	and    coalesce(nr_seq_familia,coalesce(nr_seq_familia_p,0)) = coalesce(nr_seq_familia_p,0)
	and    coalesce(cd_material,cd_material_p) = cd_material_p;	
	
	
procedure insere_regra(nr_seq_pend_regra_p bigint) is

nr_sequencia_w bigint;

BEGIN
	select	nextval('gqa_pendencia_pac_seq')
	into STRICT	nr_sequencia_w
	;
		
	insert into gqa_pendencia_pac(	nr_sequencia,
					dt_atualizacao,
					nm_usuario,
					dt_atualizacao_nrec,
					nm_usuario_nrec,
					cd_pessoa_fisica,
					nr_atendimento,
					NR_SEQ_PEND_REGRA,
					NR_PRESCRICAO)
	values (nr_sequencia_w,
					clock_timestamp(),
					nm_usuario_p,
					clock_timestamp(),
					nm_usuario_p,
					cd_pessoa_fisica_w,
					nr_atendimento_w,
					nr_seq_pend_regra_p,
					nr_prescricao_p);
	
	if (coalesce(wheb_usuario_pck.get_ie_commit, 'S') = 'S') then
		commit;
	end if;
	CALL gerar_consulta_reg_mentor(nm_usuario_p, null, nr_seq_pend_regra_p, ie_evento_p, nr_sequencia_w,null,null,null,nr_prescricao_p);
	CALL GQA_GERAR_ACAO_REGRA(nr_seq_pend_regra_p,nr_sequencia_w,nr_atendimento_w,cd_pessoa_fisica_w,nm_usuario_p, ie_evento_p);	
end;

begin


select	max(nr_atendimento),
		max(cd_pessoa_fisica)
into STRICT	nr_atendimento_w,
		cd_pessoa_fisica_w
from	atendimento_paciente
where	nr_atendimento	= nr_atendimento_p;

cd_setor_atendimento_w	:= obter_setor_atendimento(nr_atendimento_w);
qt_idade_w		:= obter_idade_pf(cd_pessoa_fisica_w,clock_timestamp(),'A');
cd_classif_setor_w	:= obter_classif_setor(cd_setor_atendimento_w);
qt_idade_dias_w		:= obter_idade_pf(cd_pessoa_fisica_w, clock_timestamp(), 'DIA');
qt_peso_gramas_w	:= (coalesce(obter_peso_pf(cd_pessoa_fisica_w),0) * 1000);

if (ie_evento_p = '7') then
	for regras in c02 loop
		select  count(1)
		into STRICT     qt_reg_w
		from    gqa_pendencia_pac a
		where   a.nr_prescricao = nr_prescricao_p
		and     NR_SEQ_PEND_REGRA = regras.nr_sequencia;
	
		if (qt_reg_w = 0) then
			insere_regra(regras.nr_sequencia);
		end if;
	end loop;
	
elsif (coalesce(ie_evento_p,'0') = '8') then
	select  case when count(*) > 0 then 1 else 0 end
	into STRICT 	qt_reg_sae_w
	from 	pe_prescricao pe,
			atendimento_paciente atend
	where 	pe.nr_atendimento = atend.nr_atendimento
	and   	pe.nr_atendimento	= nr_atendimento_p
	and 	pe.ie_situacao = 'A'
	and 	pe.ie_tipo = 'SAE'
	and 	(pe.dt_liberacao IS NOT NULL AND pe.dt_liberacao::text <> '')
	and   	coalesce(pe.dt_inativacao::text, '') = ''
	and   	coalesce(pe.dt_suspensao::text, '') = ''
	and   	coalesce(atend.dt_alta::text, '') = '';

	if (qt_reg_sae_w > 0) then	
		for regras in c01 loop
			insere_regra(regras.nr_sequencia);
		end loop;
	end if;
	
elsif (coalesce(ie_evento_p,'0') = '10') then
	
	for regras in c01 loop
		
		select GQA_regra_complementar(nm_usuario_p, nr_atendimento_p, regras.nr_sequencia)
		into STRICT ie_insert_rule_w
		;
		
		if (coalesce(ie_insert_rule_w,'Y') = 'X') then
			insere_regra(regras.nr_sequencia);
		end if;
		
	end loop;
else
	for regras in c01 loop
		insere_regra(regras.nr_sequencia);
	end loop;
end if;

if (coalesce(wheb_usuario_pck.get_ie_commit, 'S') = 'S') then commit; end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE gqa_gerar_protocolo_assist ( nr_atendimento_p bigint, ie_evento_p text, nm_usuario_p text, cd_protocolo_p bigint default 0, nr_seq_protocolo_p bigint default 0, cd_classe_material_p bigint default 0, nr_seq_familia_p bigint default 0, cd_material_p bigint default 0, nr_prescricao_p bigint default null) FROM PUBLIC;

