CREATE OR REPLACE PROCEDURE gqa_liberar_protocolo ( nr_seq_protocolo_p gqa_protocolo_pac.nr_sequencia%type ) AS $body$
DECLARE


cd_profissional_w         pessoa_fisica.cd_pessoa_fisica%type;
nm_usuario_w              gqa_protocolo_pac.nm_usuario%type;
develiberar               smallint;
dt_prevista_inicio_w      gqa_protocolo_pac.dt_prevista_inicio%type;
dt_prevista_fim_w         gqa_protocolo_pac.dt_prevista_fim%type;


BEGIN
  select obter_usuario_ativo into STRICT nm_usuario_w;

  select (obter_pf_usuario(nm_usuario_w, 'C'))::numeric  into STRICT cd_profissional_w;

  if ((nr_seq_protocolo_p IS NOT NULL AND nr_seq_protocolo_p::text <> '')
    and (nm_usuario_w IS NOT NULL AND nm_usuario_w::text <> '')
    and (cd_profissional_w IS NOT NULL AND cd_profissional_w::text <> '')) then

        select count(*) into STRICT develiberar from gqa_protocolo_pac where nr_sequencia = nr_seq_protocolo_p and coalesce(dt_liberacao::text, '') = '';

        if (develiberar = 1) then
          update gqa_protocolo_pac set dt_liberacao = clock_timestamp(), nm_usuario_liberacao = nm_usuario_w, cd_profissional = cd_profissional_w where nr_sequencia = nr_seq_protocolo_p;
          CALL gqa_liberar_protocolo_recursiv(nr_seq_protocolo_p, null);

          select min(dt_prevista_inicio), max(dt_prevista_fim) into STRICT dt_prevista_inicio_w, dt_prevista_fim_w from gqa_protocolo_etapa_pac where nr_seq_prot_pac = nr_seq_protocolo_p;
          update gqa_protocolo_pac set dt_prevista_inicio = dt_prevista_inicio_w, dt_prevista_fim = dt_prevista_fim_w where nr_sequencia = nr_seq_protocolo_p;
        end if;
  end if;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
