CREATE OR REPLACE PROCEDURE grafico_inicia_finaliza_proc ( nr_prescricao_p bigint, nr_sequencia_p bigint, ie_opcao_p text, nm_usuario_p text) AS $body$
BEGIN

if (ie_opcao_p = 'I') then
	update	prescr_procedimento
	set		dt_inicio 		= clock_timestamp(),
			nm_usuario 		= nm_usuario_p,
			dt_atualizacao	= clock_timestamp()
	where	nr_prescricao	= nr_prescricao_p
	and		nr_sequencia	= nr_sequencia_p;
elsif (ie_opcao_p = 'DI') then
	update	prescr_procedimento
	set		dt_inicio 		 = NULL,
			nm_usuario 		= nm_usuario_p,
			dt_atualizacao	= clock_timestamp()
	where	nr_prescricao	= nr_prescricao_p
	and		nr_sequencia	= nr_sequencia_p;
elsif (ie_opcao_p = 'F') then
	update	prescr_procedimento
	set		dt_fim 			= clock_timestamp(),
			nm_usuario 		= nm_usuario_p,
			dt_atualizacao	= clock_timestamp()
	where	nr_prescricao	= nr_prescricao_p
	and		nr_sequencia	= nr_sequencia_p;
elsif (ie_opcao_p = 'DF') then
	update	prescr_procedimento
	set		dt_fim 			 = NULL,
			nm_usuario 		= nm_usuario_p,
			dt_atualizacao	= clock_timestamp()
	where	nr_prescricao	= nr_prescricao_p
	and		nr_sequencia	= nr_sequencia_p;
end if;

commit;

end;
$body$
