SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE gravar_acesso_patient_bar ( nm_usuario_p text, nr_episodio_p text ) AS $body$
DECLARE


	nr_ultimo_episodio_w	episodio_paciente.nr_sequencia%type;
  	nr_seq_novo_acesso_w 	acesso_patient_bar.nr_sequencia%type;
  	nr_seq_episodio_w    	episodio_paciente.nr_sequencia%type;


BEGIN

	select	max(nr_sequencia)
	into STRICT	nr_seq_episodio_w
	from	episodio_paciente
	where	nr_episodio  = nr_episodio_p;

	if (coalesce(nr_seq_episodio_w::text, '') = '') then

		select	max(nr_sequencia)
		into STRICT	nr_seq_episodio_w
		from	episodio_paciente
		where	nr_sequencia  = nr_episodio_p;
	  
	end if;

  
	select 	max(nr_seq_episodio)
	into STRICT 	nr_ultimo_episodio_w
	from	acesso_patient_bar
	where	nm_usuario      	= nm_usuario_p
	and	nr_seq_episodio 	= nr_seq_episodio_w;


	if (coalesce(nr_ultimo_episodio_w::text, '') = '') then

		insert into acesso_patient_bar(
			nr_sequencia,
			dt_atualizacao,
			nm_usuario,
			cd_funcao,
			nr_seq_episodio
	  	)
	  	values (
			nextval('acesso_patient_bar_seq'),
			clock_timestamp(),
			nm_usuario_p,
			obter_funcao_ativa,
			nr_seq_episodio_w
	  	);
	else
		update	acesso_patient_bar
		set 	dt_atualizacao  	= clock_timestamp()
		where 	nm_usuario    	= nm_usuario_p
		and 	nr_seq_episodio 	= nr_seq_episodio_w;
	end if;

	commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
