CREATE OR REPLACE PROCEDURE gravar_agenda_paciente_envio ( nr_seq_agenda_p bigint, ds_email_p text, ds_destinatario_p text, ds_Assunto_p text, nm_usuario_p text, ie_tipo_email_p text) AS $body$
DECLARE


dt_envio_orient_w	timestamp;
dt_envio_cons_w		timestamp;
ds_email_w		varchar(4000);


BEGIN

if (ie_tipo_email_p = 'O') then

	ds_email_w := substr(ds_email_p,1,4000);


	insert into agenda_paciente_envio(
				nr_sequencia,
				dt_atualizacao,
				nm_usuario,
				dt_atualizacao_nrec,
				nm_usuario_nrec,
				nr_seq_agenda,
				dt_envio,
				ds_email,
				ds_destinatario,
				ds_assunto,
				ie_tipo_email
				)
			values (
				nextval('agenda_paciente_envio_seq'),
				clock_timestamp(),
				nm_usuario_p,
				clock_timestamp(),
				nm_usuario_p,
				nr_seq_agenda_p,
				clock_timestamp(),
				ds_email_w,
				substr(ds_destinatario_p,1,255),
				substr(ds_assunto_p,1,255),
				ie_tipo_email_p
				);

	select	max(dt_envio_orient)
	into STRICT	dt_envio_orient_w
	from	agenda_paciente
	where	nr_sequencia = nr_seq_agenda_p;

	if (coalesce(dt_envio_orient_w::text, '') = '') then
		update	agenda_paciente
		set	dt_envio_orient = clock_timestamp()
		where	nr_sequencia 	= nr_seq_agenda_p;
	end if;


elsif (ie_tipo_email_p = 'C') then
	insert into agenda_paciente_envio(
				nr_sequencia,
				dt_atualizacao,
				nm_usuario,
				dt_atualizacao_nrec,
				nm_usuario_nrec,
				nr_seq_agenda,
				dt_envio,
				ds_email_long,
				ds_destinatario,
				ds_assunto,
				ie_tipo_email
				)
			values (
				nextval('agenda_paciente_envio_seq'),
				clock_timestamp(),
				nm_usuario_p,
				clock_timestamp(),
				nm_usuario_p,
				nr_seq_agenda_p,
				clock_timestamp(),
				ds_email_p,
				substr(ds_destinatario_p,1,255),
				substr(ds_assunto_p,1,255),
				ie_tipo_email_p
				);

	select	max(dt_envio_cons)
	into STRICT	dt_envio_cons_w
	from	agenda_paciente
	where	nr_sequencia = nr_seq_agenda_p;

	if (coalesce(dt_envio_cons_w::text, '') = '') then
		update	agenda_paciente
		set	dt_envio_cons = clock_timestamp()
		where	nr_sequencia 	= nr_seq_agenda_p;
	end if;

end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
