CREATE OR REPLACE PROCEDURE gravar_agend_rothman (( nr_atendimento_p bigint, nr_seq_reg_p bigint, ie_opcao_p text, nm_usuario_p text) is nr_seq_evento_w bigint DEFAULT 0) RETURNS varchar AS $body$
BEGIN
		return ''''||ds_parametro_p||'''';
	end;

begin

if (ie_opcao_p		= 'SV') then
	nr_seq_evento_w	:= 238;
elsif (ie_opcao_p		= 'EX') then
	nr_seq_evento_w	:= 240;
else
	nr_seq_evento_w	:= 241;
end if;


if (wheb_usuario_pck.is_evento_ativo( nr_seq_evento_w ) = 'S') then

	select	max(a.ie_setor_rothman)
	into STRICT	ie_setor_rothman_w
	from	unidade_atendimento b,
			setor_atendimento a
	where	a.cd_setor_atendimento	= b.cd_setor_atendimento
	and		b.nr_atendimento	= nr_atendimento_p;

	if (ie_setor_rothman_w	= 'S') then

		ds_parametros_w	:= 	nr_atendimento_p||
							',sysdate,'||
							QuotedStr(nm_usuario_p)||','||
							QuotedStr(ie_opcao_p)||','||
							nr_seq_reg_p||','||
							obter_perfil_ativo;

		dbms_job.submit(jobno, 'GERAR_ROTHMAN_ATEND_DIA('||ds_parametros_w||');');
	end if;

end if;


end;
$body$
