SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE gravar_alteracoes_carga_tuss (nm_usuario_p text, nr_sequencia_p bigint) AS $body$
DECLARE


nr_sequencia_item_w			tuss_material_item.nr_sequencia%type;

c01 CURSOR FOR
	SELECT	cd_mat_tuss_novo,
		cd_material,
		nr_seq_tuss_mat,
		dt_vigencia_inicial,
		nr_seq_tabela_origem
	from	w_validacao_tuss_matmed
	where	nm_usuario = nm_usuario_p
	and	nr_seq_tuss_hist = nr_sequencia_p
	and	coalesce(cd_mat_tuss_novo,0) > 0;

c02 CURSOR FOR
	SELECT	nr_seq_tabela_origem,
		nr_seq_proc_interno,
		dt_vigencia_final,
		cd_proced_tuss_novo
	from	w_validacao_tuss_procserv
	where	nm_usuario = nm_usuario_p
	and	nr_seq_tuss_hist = nr_sequencia_p
	and	coalesce(cd_proced_tuss_novo,0) > 0;
BEGIN


for r_c01_w in c01 loop
	begin

	begin
	select	max(nr_sequencia)
	into STRICT	nr_sequencia_item_w
	from	tuss_material_item
	where	nr_seq_carga_tuss	= r_c01_w.nr_seq_tuss_mat
	and		cd_material_tuss	= r_c01_w.cd_mat_tuss_novo;
	exception when others then
		nr_sequencia_item_w := null;
	end;

	insert into material_tuss(
		cd_categoria,
		cd_cgc_fornecedor,
		cd_convenio,
		cd_estabelecimento,
		cd_material,
		cd_material_tuss,
		dt_atualizacao,
		dt_atualizacao_nrec,
		dt_vigencia_final,
		dt_vigencia_inicial,
		ie_origem_preco,
		ie_situacao,
		ie_tipo_atendimento,
		nm_usuario,
		nm_usuario_nrec,
		nr_seq_marca,
		nr_seq_tuss_mat,
		nr_seq_tuss_mat_item,
		nr_sequencia)
	SELECT 	cd_categoria,
		cd_cgc_fornecedor,
		cd_convenio,
		cd_estabelecimento,
		r_c01_w.cd_material,
		r_c01_w.cd_mat_tuss_novo,
		clock_timestamp(),
		clock_timestamp(),
		dt_vigencia_final,
		r_c01_w.dt_vigencia_inicial,
		ie_origem_preco,
		ie_situacao,
		ie_tipo_atendimento,
		nm_usuario_p,
		nm_usuario_p,
		nr_seq_marca,
		r_c01_w.nr_seq_tuss_mat,
		nr_sequencia_item_w,
		nextval('material_tuss_seq')
	from	material_tuss
	where	nr_sequencia = r_c01_w.nr_seq_tabela_origem;

	end;
end loop;

for r_c02_w in c02 loop
	begin

	update 	proc_interno_conv_tuss
	set 	dt_final_vigencia	= r_c02_w.dt_vigencia_final
	where 	nr_sequencia		= r_c02_w.nr_seq_tabela_origem;

	insert into proc_interno_conv_tuss(
		cd_categoria,
		cd_convenio,
		cd_edicao_amb,
		cd_estabelecimento,
		cd_procedimento,
		cd_setor_atendimento,
		cd_tipo_acomodacao,
		dt_atualizacao,
		dt_atualizacao_nrec,
		dt_final_vigencia,
		dt_inicio_vigencia,
		ie_origem_proced,
		ie_origem_proc_filtro,
		ie_situacao,
		ie_tipo_atendimento,
		nm_usuario,
		nm_usuario_nrec,
		nr_seq_proc_interno,
		nr_sequencia)
	SELECT 	cd_categoria,
		cd_convenio,
		cd_edicao_amb,
		cd_estabelecimento,
		r_c02_w.cd_proced_tuss_novo,
		cd_setor_atendimento,
		cd_tipo_acomodacao,
		clock_timestamp(),
		clock_timestamp(),
		r_c02_w.dt_vigencia_final,
		dt_inicio_vigencia,
		ie_origem_proced,
		ie_origem_proc_filtro,
		ie_situacao,
		ie_tipo_atendimento,
		nm_usuario_p,
		nm_usuario_p,
		r_c02_w.nr_seq_proc_interno,
		nextval('proc_interno_conv_tuss_seq')
	from 	proc_interno_conv_tuss
	where	nr_sequencia = r_c02_w.nr_seq_tabela_origem;

	end;
end loop;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE gravar_alteracoes_carga_tuss (nm_usuario_p text, nr_sequencia_p bigint) FROM PUBLIC;

