CREATE OR REPLACE PROCEDURE gravar_anexo_titulo_pagar_arq (nr_titulo_p bigint, ds_arquivo_p text, nm_usuario_p text) AS $body$
DECLARE


qt_anexo_w    integer;
ds_nome_arq_w   varchar(255);


BEGIN

if (nr_titulo_p IS NOT NULL AND nr_titulo_p::text <> '') and (ds_arquivo_p IS NOT NULL AND ds_arquivo_p::text <> '') and (nm_usuario_p IS NOT NULL AND nm_usuario_p::text <> '') then
	
	-- verifica se ja existe algum anexo com mesmo caminho para a mesma fatura
	select	count(*)
	into STRICT	qt_anexo_w
	from	titulo_pagar_arq
	where	nr_titulo	= nr_titulo_p
	and	ds_arquivo	= ds_arquivo_p;
	
	-- so registra o anexo, caso nao existir nenhum identico para a mesma fatura
	if (qt_anexo_w = 0) then

    insert into titulo_pagar_arq(nr_sequencia,
      dt_atualizacao,		nm_usuario,   dt_atualizacao_nrec,
      nm_usuario_nrec,	nr_titulo,    ds_arquivo)
    values (nextval('titulo_pagar_arq_seq'),
      clock_timestamp(),		nm_usuario_p,   clock_timestamp(),
      nm_usuario_p,		nr_titulo_p,    ds_arquivo_p);

		commit;
	end if;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
