CREATE OR REPLACE PROCEDURE gravar_atend_lancar_nf ( nr_sequencia_p bigint, nr_item_nf_p bigint, nr_atendimento_p bigint, dt_atendimento_p timestamp, nm_usuario_p text) AS $body$
DECLARE

 
qt_existe_atend_w			bigint;
dt_calculo_w			timestamp;
cd_paciente_w			pessoa_fisica.cd_pessoa_fisica%type;


BEGIN 
 
select	count(*) 
into STRICT	qt_existe_atend_w 
from	atendimento_paciente 
where	nr_atendimento = nr_atendimento_p;
 
if (qt_existe_atend_w = 0) then 
	/*O atendimento informado não existe! Verifique.*/
 
	CALL wheb_mensagem_pck.exibir_mensagem_abort(207899);
end if;
 
select	dt_atualizacao_estoque 
into STRICT	dt_calculo_w 
from	nota_fiscal 
where	nr_sequencia = nr_sequencia_p;
 
if (dt_calculo_w IS NOT NULL AND dt_calculo_w::text <> '') then 
	/*Esta nota fiscal já está calculada! 
	Não é permitido informar/alterar o atendimento para lançamento em NF calculada.*/
 
	CALL wheb_mensagem_pck.exibir_mensagem_abort(207906);
end if;
 
select max(cd_pessoa_fisica) 
into STRICT	cd_paciente_w 
from 	atendimento_paciente 
where	nr_atendimento = nr_atendimento_p;
 
update	nota_fiscal_item 
set	nr_atend_lancamento = nr_atendimento_p, 
	dt_atend_lancamento = dt_atendimento_p, 
	dt_atualizacao = clock_timestamp(), 
	nm_usuario = nm_usuario_p, 
	cd_paciente = cd_paciente_w 
where	nr_sequencia = nr_sequencia_p 
and	(cd_material IS NOT NULL AND cd_material::text <> '') 
and	((nr_item_nf = nr_item_nf_p) or (nr_item_nf_p = 0));
 
commit;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
