SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE gravar_atend_status_pa ( nr_atendimento_p bigint, ie_status_pa_p text, nm_usuario_p text) AS $body$
DECLARE


nr_sequencia_w	atend_status_pa.nr_sequencia%type;
ie_status_pa_w	atend_status_pa.ie_status_pa%type;


BEGIN

if (coalesce(nr_atendimento_p,0) > 0) then

	select 	coalesce(max(nr_sequencia),0)
	into STRICT	nr_sequencia_w
	from	atend_status_pa
	where	nr_atendimento = nr_atendimento_p;

	ie_status_pa_w	:= null;

	if (nr_sequencia_w > 0) then

		select	max(ie_status_pa)
		into STRICT 	ie_status_pa_w
		from	atend_status_pa
		where	nr_sequencia = nr_sequencia_w;

	end if;

	if (coalesce(ie_status_pa_w::text, '') = '') or (ie_status_pa_w <> ie_status_pa_p) then

		insert into atend_status_pa(
					nr_sequencia,
					nr_atendimento,
					dt_atualizacao,
					nm_usuario,
					dt_atualizacao_nrec,
					nm_usuario_nrec,
					ie_status_pa,
					dt_registro)
				values (nextval('atend_status_pa_seq'),
					nr_atendimento_p,
					clock_timestamp(),
					nm_usuario_p,
					clock_timestamp(),
					nm_usuario_p,
					ie_status_pa_p,
					clock_timestamp());
	end if;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
