CREATE OR REPLACE PROCEDURE gravar_aut_conv_fat_direto ( nr_seq_mat_autor_p bigint, cd_setor_fatur_direto_p bigint, nm_usuario_p text) AS $body$
BEGIN

update	material_autorizado
set	ie_faturamento_direto 	= CASE WHEN ie_faturamento_direto='N' THEN 'S'  ELSE 'N' END ,
	dt_atualizacao	      	= clock_timestamp(),
	nm_usuario	      	= nm_usuario_p,
	DT_FATUR_DIRETO		= CASE WHEN ie_faturamento_direto='N' THEN clock_timestamp()  ELSE DT_FATUR_DIRETO END ,
	NM_USUARIO_FATUR_DIRETO = CASE WHEN ie_faturamento_direto='N' THEN nm_usuario_p  ELSE NM_USUARIO_FATUR_DIRETO END ,
	CD_SETOR_FATUR_DIRETO   = CASE WHEN ie_faturamento_direto='N' THEN cd_setor_fatur_direto_p  ELSE CD_SETOR_FATUR_DIRETO END
where	nr_sequencia	      = nr_seq_mat_autor_p;

commit;

end;
