CREATE OR REPLACE PROCEDURE gravar_aut_matcir_fat_direto ( nr_seq_autorizacao_p bigint, cd_setor_fatur_direto_p bigint, nm_usuario_p text) AS $body$
DECLARE


cd_setor_w	setor_atendimento.cd_setor_atendimento%type;


BEGIN

if (cd_setor_fatur_direto_p > 0) then
	cd_setor_w := cd_setor_fatur_direto_p;
end if;



update	material_autor_cirurgia
set	ie_faturamento_direto 	= CASE WHEN coalesce(ie_faturamento_direto,'N')='N' THEN 'S'  ELSE 'N' END ,
	dt_atualizacao	      	= clock_timestamp(),
	nm_usuario	      	= nm_usuario_p,
	dt_fatur_direto		= CASE WHEN ie_faturamento_direto='N' THEN clock_timestamp()  ELSE dt_fatur_direto END ,
	nm_usuario_fatur_direto 	= CASE WHEN ie_faturamento_direto='N' THEN nm_usuario_p  ELSE nm_usuario_fatur_direto END ,
	cd_setor_fatur_direto   	= CASE WHEN ie_faturamento_direto='N' THEN cd_setor_w  ELSE cd_setor_fatur_direto END
where	nr_sequencia	    	= nr_seq_autorizacao_p;

commit;

end;
