CREATE OR REPLACE PROCEDURE gravar_barras_material ( cd_material_p bigint, cd_barras_material_p text, nm_usuario_p text) AS $body$
DECLARE


qt_existe_w		bigint;


BEGIN

select	count(1)
into STRICT	qt_existe_w
from	material_cod_barra
where	cd_material = cd_material_p
and	cd_barra_material = cd_barras_material_p;

if (qt_existe_w = 0) then

	begin
	insert into material_cod_barra(
		cd_barra_material,
		cd_material,
		dt_atualizacao,
		dt_atualizacao_nrec,
		nm_usuario,
		nm_usuario_nrec)
	values (	cd_barras_material_p,
		cd_material_p,
		clock_timestamp(),
		clock_timestamp(),
		nm_usuario_p,
		nm_usuario_p);
	exception
		when others then null;
	end;
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
