CREATE OR REPLACE PROCEDURE gravar_campo_usuario ( cd_funcao_p bigint, ds_campo_p text, ds_valor_p text, nm_usuario_p text) AS $body$
DECLARE

qt_reg_w	smallint;
nr_sequencia_w	bigint;


BEGIN

select	count(*)
into STRICT	qt_reg_w
from	usuario_funcao_campo
where	nm_usuario	= nm_usuario_p
and	cd_funcao	= cd_funcao_p
and	ds_campo	= ds_campo_p;

if (qt_reg_w > 0) then
	update	usuario_funcao_campo
	set	ds_valor	= ds_valor_p
	where	nm_usuario	= nm_usuario_p
	and	cd_funcao	= cd_funcao_p
	and	ds_campo	= ds_campo_p;
else
	select	nextval('usuario_funcao_campo_seq')
	into STRICT	nr_sequencia_w
	;

	insert	into	usuario_funcao_campo(
		nr_sequencia,
		dt_atualizacao,
		nm_usuario,
		dt_atualizacao_nrec,
		nm_usuario_nrec,
		cd_funcao,
		ds_campo,
		ds_valor)
	values (nr_sequencia_w,
		clock_timestamp(),
		nm_usuario_p,
		clock_timestamp(),
		nm_usuario_p,
		cd_funcao_p,
		ds_campo_p,
		ds_valor_p);
end	if;

commit;

end	;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
