CREATE OR REPLACE PROCEDURE gravar_componente_senha ( nm_componente_p text, qt_esquerda_p bigint, qt_topo_p bigint, qt_altura_p bigint, qt_largura_p bigint, ds_fonte_p text, ie_italico_p text, ie_negrito_p text, ie_sublinhado_p text, ie_riscado_p text, ie_centralizar_p text, ds_cor_p text, qt_tamanho_fonte_p bigint, nm_atributo_p text, ie_componente_adicional_p text default 'N', ie_quebra_linha_p text default 'N', nm_usu_salvar_p text DEFAULT 'X', nm_maquina_p text DEFAULT 'X', cd_estab_salvar_p bigint DEFAULT 0, cd_perfil_salvar_p bigint DEFAULT 0, ie_tipo_componente_p bigint DEFAULT 0, ie_tipo_informacao_p bigint DEFAULT 0) AS $body$
DECLARE


nm_componente_w		varchar(100);
qt_esquerda_w		integer;
qt_topo_w		integer;
qt_altura_w		integer;
qt_largura_w		integer;
ds_fonte_w		varchar(20);
ie_italico_w		varchar(1);
ie_negrito_w		varchar(1);
ie_sublinhado_w		varchar(1);
ie_riscado_w		varchar(1);
ie_centralizar_w		varchar(1);
ds_cor_w			varchar(20);
qt_tamanho_fonte_w	smallint;


BEGIN

SELECT	MAX(nm_componente)
INTO STRICT	nm_componente_w
FROM	monitor_senha_custom
WHERE	coalesce(nm_usuario, 'X') = coalesce(nm_usu_salvar_p, 'X')
AND	coalesce(nm_maquina, 'X') = coalesce(nm_maquina_p, 'X')
AND	coalesce(cd_perfil, 0) = coalesce(cd_perfil_salvar_p, 0)
AND	coalesce(cd_estabelecimento, 0) = coalesce(cd_estab_salvar_p, 0)
AND	nm_componente = nm_componente_p;

IF (nm_componente_w IS NOT NULL AND nm_componente_w::text <> '') THEN

	UPDATE	monitor_senha_custom
	SET	qt_esquerda = qt_esquerda_p,
		qt_topo = qt_topo_p,
		qt_altura = qt_altura_p,
		qt_largura = qt_largura_p,
		ds_fonte = ds_fonte_p,
		ie_italico = ie_italico_p,
		ie_negrito = ie_negrito_p,
		ie_sublinhado = ie_sublinhado_p,
		ie_riscado = ie_riscado_p,
		ie_centralizar = ie_centralizar_p,
		ds_cor = ds_cor_p,
		qt_tamanho_fonte = qt_tamanho_fonte_p,
		ie_componente_adicional = ie_componente_adicional_p,
		nm_atributo = nm_atributo_p,
		ie_quebra_linha = ie_quebra_linha_p,
		ie_tipo_informacao = ie_tipo_informacao_p
	WHERE	coalesce(nm_usuario, 'X') = coalesce(nm_usu_salvar_p, 'X')
	AND	coalesce(nm_maquina, 'X') = coalesce(nm_maquina_p, 'X')
	AND	coalesce(cd_perfil, 0) = coalesce(cd_perfil_salvar_p, 0)
	AND	coalesce(cd_estabelecimento, 0) = coalesce(cd_estab_salvar_p, 0)
	AND	nm_componente = nm_componente_p;
ELSE
	INSERT	INTO	monitor_senha_custom(nm_componente,
		nm_usuario,
		nm_maquina,
		cd_perfil,
		cd_estabelecimento,
		qt_esquerda,
		qt_topo,
		qt_altura,
		qt_largura,
		ds_fonte,
		ie_italico,
		ie_negrito,
		ie_sublinhado,
		ie_riscado,
		ie_centralizar,
		ds_cor,
		qt_tamanho_fonte,
		ie_componente_adicional,
		nm_atributo,
		ie_quebra_linha,
		ie_tipo_componente,
		ie_tipo_informacao)
	VALUES (nm_componente_p,
		nm_usu_salvar_p,
		nm_maquina_p,
		cd_perfil_salvar_p,
		cd_estab_salvar_p,
		qt_esquerda_p,
		qt_topo_p,
		qt_altura_p,
		qt_largura_p,
		ds_fonte_p,
		ie_italico_p,
		ie_negrito_p,
		ie_sublinhado_p,
		ie_riscado_p,
		ie_centralizar_p,
		ds_cor_p,
		qt_tamanho_fonte_p,
		ie_componente_adicional_p,
		nm_atributo_p,
		ie_quebra_linha_p,
		ie_tipo_componente_p,
		ie_tipo_informacao_p);
END IF;

COMMIT;

END;
$body$
