CREATE OR REPLACE PROCEDURE gravar_comprador_solic_compra (cd_comprador_resp_p text, nr_solic_compra_p bigint, nm_usuario_p text) AS $body$
DECLARE

 
cd_estabelecimento_w			smallint;
cd_comprador_resp_w			varchar(10);
nm_comprador_resp_w			comprador.nm_guerra%type;
nm_comprador_resp_ww			comprador.nm_guerra%type;
ds_titulo_w				varchar(80);
ds_historico_w				varchar(4000);
						

BEGIN 
 
select	coalesce(cd_comprador_resp,'0'), 
	substr(sup_obter_nome_comprador(cd_estabelecimento,cd_comprador_resp),1,80), 
	substr(sup_obter_nome_comprador(cd_estabelecimento,cd_comprador_resp_p),1,80), 
	cd_estabelecimento 
into STRICT	cd_comprador_resp_w, 
	nm_comprador_resp_w, 
	nm_comprador_resp_ww, 
	cd_estabelecimento_w 
from	solic_compra 
where	nr_solic_compra = nr_solic_compra_p;
 
update	solic_compra 
set	cd_comprador_resp = cd_comprador_resp_p, 
	nm_usuario = nm_usuario_p, 
	dt_atualizacao = clock_timestamp() 
where	nr_solic_compra	= nr_solic_compra_p;
 
if (cd_comprador_resp_w = '0') then 
	ds_titulo_w 	:= 	WHEB_MENSAGEM_PCK.get_texto(302610);
	ds_historico_w	:= 	WHEB_MENSAGEM_PCK.get_texto(302611,'NM_COMPRADOR_RESP_WW='||nm_comprador_resp_ww);
else 
	ds_titulo_w	:= 	WHEB_MENSAGEM_PCK.get_texto(302612);
	ds_historico_w	:= 	WHEB_MENSAGEM_PCK.get_texto(302613,'NM_USUARIO_P='||nm_usuario_p|| 
						';NM_COMPRADOR_RESP_W='||nm_comprador_resp_w|| 
						';NM_COMPRADOR_RESP_WW='||nm_comprador_resp_ww);
end if;
 
CALL gerar_historico_solic_compra(nr_solic_compra_p,ds_titulo_w,ds_historico_w,'A',nm_usuario_p);
CALL gerar_comunic_comprador_sc(nr_solic_compra_p, nm_usuario_p);
 
commit;
 
end	;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
