CREATE OR REPLACE PROCEDURE gravar_config_painel ( ds_cor_fonte_p text, ds_fonte_p text, ds_estilo_fonte_p text, qt_tamanho_fonte_p bigint, nm_usuario_p text, ie_opcao_p text) AS $body$
DECLARE


ds_erro_w	varchar(255);


BEGIN

begin
if (ie_opcao_p = 'D') then
		update 	agenda_config_painel
		set		ds_cor_fonte		= ds_cor_fonte_p,
				ds_fonte			= ds_fonte_p,
				ds_estilo_fonte		= ds_estilo_fonte_p,
				qt_tamanho_fonte	= qt_tamanho_fonte_p,
				dt_atualizacao		= clock_timestamp()
		where	nm_usuario		= nm_usuario_p;

	if (NOT FOUND) then
		insert into agenda_config_painel(
			nr_sequencia,
			dt_atualizacao,
			nm_usuario,
			dt_atualizacao_nrec,
			nm_usuario_nrec,
			ds_cor_fonte,
			ds_fonte,
			ds_estilo_fonte,
			qt_tamanho_fonte)
		values (
			nextval('agenda_config_painel_seq'),
			clock_timestamp(),
			nm_usuario_p,
			clock_timestamp(),
			nm_usuario_p,
			ds_cor_fonte_p,
			ds_fonte_p,
			ds_estilo_fonte_p,
			qt_tamanho_fonte_p);
	end if;

elsif (ie_opcao_p = 'J') then
		update 	agenda_config_painel
		set		ds_cor_fonte_java		= ds_cor_fonte_p,
				ds_fonte_java			= ds_fonte_p,
				ds_estilo_fonte_java	= ds_estilo_fonte_p,
				qt_tamanho_fonte		= qt_tamanho_fonte_p,
				dt_atualizacao			= clock_timestamp()
		where	nm_usuario				= nm_usuario_p;

		if (NOT FOUND) then
		insert into agenda_config_painel(
			nr_sequencia,
			dt_atualizacao,
			nm_usuario,
			dt_atualizacao_nrec,
			nm_usuario_nrec,
			ds_cor_fonte_java,
			ds_fonte_java,
			ds_estilo_fonte_java,
			qt_tamanho_fonte)
		values (
			nextval('agenda_config_painel_seq'),
			clock_timestamp(),
			nm_usuario_p,
			clock_timestamp(),
			nm_usuario_p,
			ds_cor_fonte_p,
			ds_fonte_p,
			ds_estilo_fonte_p,
			qt_tamanho_fonte_p);
		end if;

end if;

exception
when others then
	ds_erro_w		:= sqlerrm(SQLSTATE);
	-- Nao Atualizou. Erro: ' || ds_erro_w
	CALL Wheb_mensagem_pck.exibir_mensagem_abort(264498,'DS_ERRO_W=' || DS_ERRO_W);
end;

commit;

end;
$body$
