CREATE OR REPLACE PROCEDURE gravar_conf_grafico_pepo ( ie_opcao_padrao_p text, ds_campo_p text, nm_usuario_p text) AS $body$
DECLARE


ie_possui_registro_w	varchar(1);


BEGIN

select	coalesce(max('S'),'N')
into STRICT	ie_possui_registro_w
from	pepo_configuracao_agente
where	nm_usuario = nm_usuario_p
and		ds_campo = ds_campo_p;

if (ie_possui_registro_w = 'N') then
	begin
	insert into pepo_configuracao_agente(
		nr_sequencia,
		dt_atualizacao,
		nm_usuario,
		dt_atualizacao_nrec,
		nm_usuario_nrec,
		ie_opcao_padrao ,
		ds_campo)
	values (	nextval('pepo_configuracao_agente_seq'),
			clock_timestamp(),
			nm_usuario_p,
			clock_timestamp(),
			nm_usuario_p,
			ie_opcao_padrao_p,
			ds_campo_p);
	end;
else
	begin
	update	pepo_configuracao_agente
	set		ie_opcao_padrao = ie_opcao_padrao_p,
			ds_campo  = ds_campo_p ,
			dt_atualizacao_nrec = clock_timestamp()
	where	nm_usuario = nm_usuario_p
	and		ds_campo = ds_campo_p;
	end;
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
