CREATE OR REPLACE PROCEDURE gravar_conpaci_log ( ds_descricao_p text, nr_interno_conta_p bigint, nm_usuario_p text) AS $body$
DECLARE

 
qt_conta_w	bigint;

BEGIN
 
select	count(*) 
into STRICT	qt_conta_w 
from	conta_paciente 
where	nr_interno_conta = nr_interno_conta_p;
 
if (qt_conta_w > 0) then 
 
	insert into conpaci_log_estorno( nr_sequencia, 
					dt_atualizacao, 
					nm_usuario, 
					dt_atualizacao_nrec, 
					nm_usuario_nrec, 
					nr_interno_conta, 
					ds_log) 
				values (nextval('conpaci_log_estorno_seq'), 
					clock_timestamp(), 
					nm_usuario_p, 
					clock_timestamp(), 
					nm_usuario_p, 
					nr_interno_conta_p, 
					ds_descricao_p);
end if;
 
--NÃ£o commitar, rotina chamada na Estornar_Conta_Paciente. 
--commit; 
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
