CREATE OR REPLACE PROCEDURE gravar_consist_conta_ipasgo ( dt_mesano_referencia_p protocolo_convenio.dt_mesano_referencia%type, cd_convenio_p convenio.cd_convenio%type, nr_interno_conta_p conta_paciente.nr_interno_conta%type, cd_inconsistencia_p ipasgo_consistencia.cd_inconsistencia%type, ds_detalhe_p ipasgo_consistencia_conta.ds_detalhe%type, cd_estabelecimento_p estabelecimento.cd_estabelecimento%type, nm_usuario_p usuario.nm_usuario%type) AS $body$
DECLARE

	nr_sequencia_consistencia_w	bigint;
	nr_seq_consistencia_conta_w	bigint;

BEGIN
	begin
		select	coalesce(nr_sequencia,0)
		into STRICT	nr_sequencia_consistencia_w
		from	ipasgo_consistencia ic
		where	ic.cd_inconsistencia = cd_inconsistencia_p;
	exception
	when no_data_found then
		nr_sequencia_consistencia_w     := 0;
	end;
	
	if (nr_sequencia_consistencia_w > 0) then
		select  nextval('ipasgo_consistencia_conta_seq')
		into STRICT    nr_seq_consistencia_conta_w	
		;

		insert into ipasgo_consistencia_conta(
                        nr_sequencia,
                        dt_mesano_referencia,
                        cd_convenio,
                        nr_interno_conta,
                        nr_seq_consistencia,
                        cd_estabelecimento,
                        ds_detalhe,
                        dt_atualizacao,
                        dt_atualizacao_nrec,
                        nm_usuario,
                        nm_usuario_nrec)
		values ( nr_seq_consistencia_conta_w,
                        dt_mesano_referencia_p,
                        cd_convenio_p,
                        nr_interno_conta_p,
                        nr_sequencia_consistencia_w,
                        cd_estabelecimento_p,
                        ds_detalhe_p,
                        clock_timestamp(),
                        clock_timestamp(),
                        nm_usuario_p,
                        nm_usuario_p);
	end if;
exception
when others then
        CALL wheb_mensagem_pck.exibir_mensagem_abort(343570, 'ERRO=' || sqlerrm);
end;
$body$
