CREATE OR REPLACE PROCEDURE gravar_consulta_telemedicina ( nr_atendimento_p bigint, nr_seq_agenda_p bigint, ie_etapa_p text, ds_url_telemed_p text, nm_usuario_p text) AS $body$
DECLARE


ie_iniciado_w	varchar(1) := 'N';


BEGIN

if (coalesce(nr_seq_agenda_p,0) > 0) then
	begin
	if (ie_etapa_p = 'I') then
		begin
		select	coalesce(max('S'),'N')
		into STRICT	ie_iniciado_w
		from	pep_tlc_registros
		where	nr_seq_agenda = nr_seq_agenda_p;

		if (ie_iniciado_w = 'N') then
			begin
			insert into pep_tlc_registros(
				nr_sequencia,
				dt_atualizacao,
				nm_usuario,
				dt_atualizacao_nrec,
				nm_usuario_nrec,
				nr_atendimento,
				nr_seq_agenda,
				dt_inicio_consulta,
				ds_url_telemedicina)
			values (
				nextval('pep_tlc_registros_seq'),
				clock_timestamp(),
				nm_usuario_p,
				clock_timestamp(),
				nm_usuario_p,
				nr_atendimento_p,
				nr_seq_agenda_p,
				clock_timestamp(),
				ds_url_telemed_p);
			end;
		end if;
		end;
	elsif (ie_etapa_p = 'F') then
		begin
		update	pep_tlc_registros
		set	dt_fim_consulta = clock_timestamp()
		where	nr_seq_agenda = nr_seq_agenda_p;
		end;
	end if;
	end;
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
