CREATE OR REPLACE PROCEDURE gravar_convenio_retorno_imp (nr_seq_retorno_p bigint, cd_autorizacao_p text, vl_pago_p bigint, vl_adic_p bigint, nr_interno_conta_p text, nm_usuario_p text) AS $body$
DECLARE


qt_existe_w		bigint := 0;
nr_seq_ret_item_w	bigint;
vl_guia_w		double precision;
vl_saldo_w		double precision;
vl_total_pago_w		double precision;

vl_adic_w		convenio_retorno_item.vl_adicional%type;


BEGIN

select	max(a.nr_sequencia),
	max(b.vl_guia)
into STRICT	nr_seq_ret_item_w,
	vl_guia_w
from	conta_paciente_guia b,
	convenio_retorno_item a
where	a.nr_seq_retorno	= nr_seq_retorno_p
and	a.cd_autorizacao	= cd_autorizacao_p
and	a.nr_interno_conta	= nr_interno_conta_p
and	b.cd_autorizacao	= a.cd_autorizacao
and	b.nr_interno_conta	= a.nr_interno_conta;

select 	sum(coalesce(vl_pago,0) + coalesce(vl_glosado,0) + coalesce(vl_desconto,0)),
	sum(coalesce(vl_adicional,0))
into STRICT	vl_total_pago_w,
	vl_adic_w
from 	convenio_retorno_item
where 	nr_interno_conta = nr_interno_conta_p;



vl_saldo_w	:= vl_guia_w - vl_total_pago_w;

if (nr_seq_ret_item_w IS NOT NULL AND nr_seq_ret_item_w::text <> '') and (vl_saldo_w > 0) then

	update	convenio_retorno_item
	set	vl_pago		= vl_pago + vl_pago_p,
		vl_amenor	= vl_saldo_w - vl_pago_p
	where	nr_sequencia	= nr_seq_ret_item_w;

end if;

if (coalesce(vl_adic_p,0) > 0) then
	update 	convenio_retorno_item
	set	vl_adicional	= vl_adic_p + vl_adic_w
	where	nr_sequencia	= nr_seq_ret_item_w;
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
