SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE gravar_cronograma_workspace ( nr_seq_cronograma_p bigint, ds_json_p text, nm_usuario_p text) AS $body$
DECLARE


ie_existe_w bigint;


BEGIN
  select  count(nr_sequencia)
  into STRICT    ie_existe_w
  from    proj_cron_workspace
  where   nr_seq_cronograma = nr_seq_cronograma_p
  and     ie_liberado = 'N';

  if (ie_existe_w = 0) then
    insert into proj_cron_workspace(
                nr_sequencia,
                nr_seq_cronograma,
                ds_json,
                dt_atualizacao,
                nm_usuario,
                dt_atualizacao_nrec,
                nm_usuario_nrec,
                ie_liberado)
    values (    nextval('proj_cron_workspace_seq'),
                nr_seq_cronograma_p,
                ds_json_p,
                clock_timestamp(),
                nm_usuario_p,
                clock_timestamp(),
                nm_usuario_p,
                'N');
  else
    update proj_cron_workspace set  ds_json = ds_json_p,
                                    dt_atualizacao = clock_timestamp(),
                                    nm_usuario = nm_usuario_p
    where nr_seq_cronograma = nr_seq_cronograma_p
    and ie_liberado = 'N';
  end if;

  commit;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
