SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE gravar_dados_bancarios_sc ( nr_solic_compra_p bigint, nm_usuario_p text) AS $body$
DECLARE


cd_banco_w		pessoa_juridica_conta.cd_banco%type;
cd_agencia_bancaria_w	pessoa_juridica_conta.cd_agencia_bancaria%type;
nr_conta_w		pessoa_juridica_conta.nr_conta%type;
nr_digito_conta_w	pessoa_juridica_conta.nr_digito_conta%type;
ie_tipo_servico_w	solic_compra.ie_tipo_servico%type;
cd_pessoa_fisica_w	solic_compra.cd_pessoa_fisica%type;
cd_fornec_sugerido_w	solic_compra.cd_fornec_sugerido%type;

c01 CURSOR FOR
SELECT	cd_banco,
	cd_agencia_bancaria,
	nr_conta,
	nr_digito_conta
from	pessoa_fisica_conta
where	cd_pessoa_fisica = cd_pessoa_fisica_w;


c02 CURSOR FOR
SELECT	cd_banco,
	cd_agencia_bancaria,
	nr_conta,
	nr_digito_conta
from	pessoa_juridica_conta
where	cd_cgc = cd_fornec_sugerido_w;


BEGIN

cd_banco_w			:= null;
cd_agencia_bancaria_w		:= '';
nr_conta_w			:= '';
nr_digito_conta_w			:= '';

select	ie_tipo_servico,
	cd_pessoa_fisica,
	cd_fornec_sugerido
into STRICT	ie_tipo_servico_w,
	cd_pessoa_fisica_w,
	cd_fornec_sugerido_w
from	solic_compra
where	nr_solic_compra = nr_solic_compra_p;

if (ie_tipo_servico_w  ('SP','SR','CD')) then

	if (cd_pessoa_fisica_w IS NOT NULL AND cd_pessoa_fisica_w::text <> '') then

		open C01;
		loop
		fetch C01 into
			cd_banco_w,
			cd_agencia_bancaria_w,
			nr_conta_w,
			nr_digito_conta_w;
		EXIT WHEN NOT FOUND; /* apply on C01 */
			begin
			cd_banco_w			:= cd_banco_w;
			cd_agencia_bancaria_w		:= cd_agencia_bancaria_w;
			nr_conta_w			:= nr_conta_w;
			nr_digito_conta_w			:= nr_digito_conta_w;
			end;
		end loop;
		close C01;

		update	solic_compra
		set	cd_banco		= cd_banco_w
		where	nr_solic_compra		= nr_solic_compra_p
		and	coalesce(cd_banco::text, '') = '';

		update	solic_compra
		set	cd_agencia_bancaria	= cd_agencia_bancaria_w
		where	nr_solic_compra		= nr_solic_compra_p
		and	coalesce(cd_agencia_bancaria::text, '') = '';


		update	solic_compra
		set	nr_conta		= nr_conta_w
		where	nr_solic_compra		= nr_solic_compra_p
		and	coalesce(nr_conta::text, '') = '';

		update	solic_compra
		set	nr_digito_conta		= nr_digito_conta_w
		where	nr_solic_compra		= nr_solic_compra_p
		and	coalesce(nr_digito_conta::text, '') = '';


	elsif (cd_fornec_sugerido_w IS NOT NULL AND cd_fornec_sugerido_w::text <> '') then
		open C02;
		loop
		fetch C02 into
			cd_banco_w,
			cd_agencia_bancaria_w,
			nr_conta_w,
			nr_digito_conta_w;
		EXIT WHEN NOT FOUND; /* apply on C02 */
			begin
			cd_banco_w			:= cd_banco_w;
			cd_agencia_bancaria_w		:= cd_agencia_bancaria_w;
			nr_conta_w			:= nr_conta_w;
			nr_digito_conta_w			:= nr_digito_conta_w;
			end;
		end loop;
		close C02;

		update	solic_compra
		set	cd_banco		= cd_banco_w
		where	nr_solic_compra		= nr_solic_compra_p
		and	coalesce(cd_banco::text, '') = '';

		update	solic_compra
		set	cd_agencia_bancaria	= cd_agencia_bancaria_w
		where	nr_solic_compra		= nr_solic_compra_p
		and	coalesce(cd_agencia_bancaria::text, '') = '';


		update	solic_compra
		set	nr_conta		= nr_conta_w
		where	nr_solic_compra		= nr_solic_compra_p
		and	coalesce(nr_conta::text, '') = '';

		update	solic_compra
		set	nr_digito_conta		= nr_digito_conta_w
		where	nr_solic_compra		= nr_solic_compra_p
		and	coalesce(nr_digito_conta::text, '') = '';
	end if;

end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE gravar_dados_bancarios_sc ( nr_solic_compra_p bigint, nm_usuario_p text) FROM PUBLIC;

