CREATE OR REPLACE PROCEDURE gravar_data_liberacao_cih ( nr_prescricao_p bigint, nr_seq_material_p bigint, qt_dias_liberado_p bigint ) AS $body$
BEGIN
	if (nr_prescricao_p IS NOT NULL AND nr_prescricao_p::text <> '')
		and (nr_seq_material_p IS NOT NULL AND nr_seq_material_p::text <> '')
		and (qt_dias_liberado_p > 0) then
		
		UPDATE		prescr_material
		SET			dt_liberacao 		= clock_timestamp(),
					qt_dias_liberado	= qt_dias_liberado_p
		WHERE		nr_prescricao		= nr_prescricao_p
		AND 		nr_sequencia		= nr_seq_material_p;
		
	end if;	

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
