CREATE OR REPLACE PROCEDURE gravar_depto_setor_agenda_cir ( nr_seq_agenda_p bigint, cd_depto_medico_p bigint, cd_setor_atendimento_p bigint, nm_usuario_p text, ie_posicao_p bigint, nr_seq_classif_atend_p bigint) AS $body$
DECLARE


nr_sequence_w		bigint;
nr_reg_w			smallint;


BEGIN
select 	count(1)
into STRICT	nr_reg_w
from	agenda_paciente_auxiliar
where 	nr_seq_agenda = nr_seq_agenda_p;

if (nr_reg_w = 0) then
	select	nextval('agenda_paciente_auxiliar_seq')
	into STRICT	nr_sequence_w
	;

	insert into agenda_paciente_auxiliar(
			nr_sequencia,
			dt_atualizacao,
			nm_usuario,
			dt_atualizacao_nrec,
			nm_usuario_nrec,
			nr_seq_agenda,
			cd_depto_medico,
			cd_setor_atendimento,
			ie_posicao,
			nr_seq_classif_atend)
		values ( nr_sequence_w,
			clock_timestamp(),
			nm_usuario_p,
			clock_timestamp(),
			nm_usuario_p,
			nr_seq_agenda_p,
			cd_depto_medico_p,
			cd_setor_atendimento_p,
			ie_posicao_p,
			nr_seq_classif_atend_p);
else
	update agenda_paciente_auxiliar
	set	cd_depto_medico = cd_depto_medico_p,
		cd_setor_atendimento = cd_setor_atendimento_p,
		nm_usuario = nm_usuario_p,
		dt_atualizacao = clock_timestamp(),
		ie_posicao = ie_posicao_p,
		nr_seq_classif_atend = nr_seq_classif_atend_p
	where nr_seq_agenda = nr_seq_agenda_p;
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
