SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE gravar_entrega_ag_opme ( nr_seq_agenda_p bigint, nm_usuario_p text) AS $body$
DECLARE

 
 
nr_sequencia_w	bigint;
qt_existe_w	integer;

 
c01 CURSOR FOR 
SELECT	a.nr_sequencia 
from	agenda_paciente a, 
	cirurgia b 
where	a.nr_cirurgia = b.nr_cirurgia 
and	(a.ie_opme_integracao IS NOT NULL AND a.ie_opme_integracao::text <> '') 
and 	(b.nr_cirurgia IS NOT NULL AND b.nr_cirurgia::text <> '') 
and 	(b.dt_termino IS NOT NULL AND b.dt_termino::text <> '') 
and 	b.dt_inicio_real < (clock_timestamp() - interval '3 days') 
and (nr_seq_agenda_p = a.nr_sequencia or nr_seq_agenda_p = 0) 
and	not exists ( SELECT	1 
	      	from	agenda_pac_opme x 
		where	a.nr_sequencia = x.nr_seq_agenda 
		and	x.ie_integracao = 'S') 
order by	nr_sequencia;


BEGIN 
if (coalesce(nr_seq_agenda_p, 0) = 0) then 
	begin 
	open C01;
	loop 
	fetch C01 into	 
		nr_sequencia_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
		CALL ajusta_status_agenda_int_opme(nr_sequencia_w, '60', null, nm_usuario_p);
	end loop;
	close c01;
	end;
end if;
 
 
if (coalesce(nr_seq_agenda_p, 0) > 0) then 
	begin 
 
	select	count(*) 
	into STRICT	qt_existe_w 
	from	agenda_paciente a, 
		cirurgia b 
	where	a.nr_cirurgia = b.nr_cirurgia 
	and	(a.ie_opme_integracao IS NOT NULL AND a.ie_opme_integracao::text <> '') 
	and	a.nr_sequencia = nr_seq_agenda_p;
	if (qt_existe_w = 0) then 
		CALL wheb_mensagem_pck.exibir_mensagem_abort(266042);
		--'Este agendamento ainda n√£o tem cirurgia gerada' 
	end if;
 
	CALL ajusta_status_agenda_int_opme(nr_seq_agenda_p, '60', null, nm_usuario_p);
	end;
end if;
 
commit;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE gravar_entrega_ag_opme ( nr_seq_agenda_p bigint, nm_usuario_p text) FROM PUBLIC;

