CREATE OR REPLACE PROCEDURE gravar_envio_res (ds_xml_p text, nr_seq_log_p bigint) AS $body$
DECLARE


ds_observacao_w varchar(255);
nr_sequencia_w LOG_INTEGRACAO_EVENTO.nr_sequencia%type;
nr_sequencia_clob_w LOG_INTEGRACAO_EVENTO_CLOB.nr_sequencia%type;


BEGIN

select nextval('log_integracao_evento_seq')
into STRICT nr_sequencia_w
;

select nextval('log_integracao_evento_clob_seq')
into STRICT nr_sequencia_clob_w
;

ds_observacao_w := 'Informacao gerada no campo ds_log ' || chr(13) ||'Script para consulta: '|| chr(13) ||'select a.ds_log, a.* '|| chr(13) ||'from LOG_INTEGRACAO_EVENTO_CLOB a'|| chr(13) ||'where NR_SEQUENCIA = ' || nr_sequencia_clob_w;

insert into LOG_INTEGRACAO_EVENTO(NM_USUARIO,
                                  IE_ENVIO_RETORNO,
                                  IE_TIPO_EVENTO,
                                  DT_ATUALIZACAO,
                                  NR_SEQ_LOG,
                                  NR_SEQUENCIA,
                                  DS_OBSERVACAO)
                          values ('Gerenciador',
                                  'E',
                                  'I',
                                  clock_timestamp(),
                                  nr_seq_log_p,
                                  nr_sequencia_w,
                                  ds_observacao_w
                                  );

insert into LOG_INTEGRACAO_EVEN(NR_SEQUENCIA,
                                        DT_ATUALIZACAO,
                                        NM_USUARIO,
                                        NR_SEQ_INTEGRACAO_EVENTO, 
                                        DS_LOG) 
                                        values (
                                        nr_sequencia_clob_w,
                                        clock_timestamp(),
                                        'Gerenciador',
                                        nr_sequencia_w,
                                        ds_xml_p);
commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
