CREATE OR REPLACE PROCEDURE gravar_erro_prescricao ( nr_prescricao_p bigint, nr_seq_medic_p bigint, nr_seq_proced_p bigint, nr_seq_solucao_p bigint, ie_origem_consist_p text, nr_seq_consist_p bigint, nr_seq_consist_cli_p bigint, ie_liberar_p text, ds_inf_adic_p text, nm_usuario_p text) AS $body$
DECLARE


nr_sequencia_w			bigint;
ie_libera_prescr_w		varchar(15);
ds_mensagem_w			varchar(4000);
ds_regra_prescr_w		varchar(255);
ie_usuario_freq_w		varchar(1)	:= 'N';


BEGIN

select	nextval('prescr_medica_erro_seq')
into STRICT	nr_sequencia_w
;

select	obter_desc_expressao(cd_exp_descricao,ds_regra_prescr),
		obter_desc_expressao(cd_exp_mensagem,ds_mensagem_usuario),
		coalesce(ie_liberar_p,ie_libera_prescr)
into STRICT	ds_regra_prescr_w,
	ds_mensagem_w,
	ie_libera_prescr_w
from	regra_consiste_prescr
where	nr_sequencia	= nr_seq_consist_p;

if (nr_seq_consist_cli_p > 0) then
	select	coalesce(ie_forma_consistencia,ie_libera_prescr_w),
		coalesce(ds_mensagem_cliente,ds_mensagem_w),
		coalesce(ie_usuario_freq,ie_usuario_freq_w)
	into STRICT	ie_libera_prescr_w,
		ds_mensagem_w,
		ie_usuario_freq_w
	from	regra_consiste_prescr_par
	where	nr_sequencia	= nr_seq_consist_cli_p;

end if;

insert into prescr_medica_erro(
	nr_sequencia,
	dt_atualizacao,
	nm_usuario,
	dt_atualizacao_nrec,
	nm_usuario_nrec,
	nr_prescricao,
	ds_inconsistencia,
	ie_libera,
	ie_tipo_regra,
	ds_erro,
	ds_inf_adicional,
	nr_seq_medic,
	nr_seq_proced)
values (	nr_sequencia_w,
	clock_timestamp(),
	nm_usuario_p,
	clock_timestamp(),
	nm_usuario_p,
	nr_prescricao_p,
	substr(ds_regra_prescr_w,1,255),
	substr(ie_libera_prescr_w,1,1),
	ie_origem_consist_p,
	substr(ds_mensagem_w,1,4000),
	substr(ds_inf_adic_p,1,255),
	nr_seq_medic_p,
	nr_seq_proced_p);


END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
