CREATE OR REPLACE PROCEDURE gravar_estilo_grid_usuario ( cd_estabelecimento_p bigint, nm_usuario_p text, nm_tabela_p text, nr_seq_visao_p bigint, nr_seq_objeto_p bigint, ie_negrito_p text, ie_italico_p text, ie_negrito_italico_p text, ie_sublinhado_p text, ie_riscado_p text, ds_fonte_p text, ds_cor_p text, qt_tam_fonte_p bigint) AS $body$
DECLARE


qt_registro_w	bigint;
nr_seq_visao_w	bigint;
nr_seq_objeto_w	bigint;


BEGIN

if (coalesce(nr_seq_visao_p::text, '') = '' and coalesce(nm_tabela_p::text, '') = '' and coalesce(nr_seq_objeto_p::text, '') = '') then
	CALL Wheb_mensagem_pck.exibir_mensagem_abort(279848);
end if;

if (nr_seq_visao_p IS NOT NULL AND nr_seq_visao_p::text <> '' AND nm_tabela_p IS NOT NULL AND nm_tabela_p::text <> '') then
	select	count(*)
	into STRICT	qt_registro_w
	from	estilo_grid_usuario
	where	nm_usuario_ref = nm_usuario_p
	and	cd_estabelecimento = cd_estabelecimento_p
	and	nr_seq_visao = nr_seq_visao_p
	and	nm_tabela = nm_tabela_p;
elsif (nm_tabela_p IS NOT NULL AND nm_tabela_p::text <> '') then
	select	count(*)
	into STRICT	qt_registro_w
	from	estilo_grid_usuario
	where	nm_usuario_ref = nm_usuario_p
	and	cd_estabelecimento = cd_estabelecimento_p
	and	nm_tabela = nm_tabela_p
	and	coalesce(nr_seq_visao::text, '') = '';
elsif (nr_seq_objeto_p IS NOT NULL AND nr_seq_objeto_p::text <> '') then
	select	count(*)
	into STRICT	qt_registro_w
	from	estilo_grid_usuario
	where	nm_usuario_ref = nm_usuario_p
	and	cd_estabelecimento = cd_estabelecimento_p
	and	nr_seq_objeto = nr_seq_objeto_p;
end if;

if (qt_registro_w = 0) then

	insert into estilo_grid_usuario(
		cd_estabelecimento,
		dt_atualizacao,
		nm_usuario,
		dt_atualizacao_nrec,
		nm_usuario_nrec,
		nm_tabela,
		nr_seq_visao,
		nr_seq_objeto,
		nm_usuario_ref,
		ie_negrito,
		ie_italico,
		ie_negrito_italico,
		ie_sublinhado,
		ie_riscado,
		ds_fonte,
		ds_cor,
		qt_tam_fonte
	) values (
		cd_estabelecimento_p,
		clock_timestamp(),
		nm_usuario_p,
		clock_timestamp(),
		nm_usuario_p,
		nm_tabela_p,
		nr_seq_visao_p,
		nr_seq_objeto_p,
		nm_usuario_p,
		ie_negrito_p,
		ie_italico_p,
		ie_negrito_italico_p,
		ie_sublinhado_p,
		ie_riscado_p,
		ds_fonte_p,
		ds_cor_p,
		qt_tam_fonte_p
	);

else

	update 	estilo_grid_usuario
	set	dt_atualizacao       = clock_timestamp(),
		nm_usuario           = nm_usuario_p,
		ie_negrito           = ie_negrito_p,
		ie_italico           = ie_italico_p,
		ie_negrito_italico   = ie_negrito_italico_p,
		ie_sublinhado        = ie_sublinhado_p,
		ie_riscado           = ie_riscado_p,
		ds_fonte             = ds_fonte_p,
		ds_cor               = ds_cor_p,
		qt_tam_fonte         = qt_tam_fonte_p
	where (coalesce(nr_seq_objeto::text, '') = '' or nr_seq_objeto = nr_seq_objeto_p)
	and (coalesce(nr_seq_visao::text, '') = '' or nr_seq_visao = nr_seq_visao_p)
	and (coalesce(nm_tabela::text, '') = '' or nm_tabela = nm_tabela_p)
	and	cd_estabelecimento = cd_estabelecimento_p;

end if;

