CREATE OR REPLACE PROCEDURE gravar_filtro_eis_usuario (nr_seq_wheb_p bigint, nr_sequencia_p bigint, nm_usuario_p text, ds_filtro_p text, nm_filtro_p text, ds_sql_filtro_p text, cd_perfil_p text, ie_dashboard_p text default null, ie_processo_p text default null, ds_dimensao_itens_p text default null) AS $body$
