CREATE OR REPLACE PROCEDURE gravar_gedipa_estoque_cab ( nm_usuario_p text, cd_material_p bigint, qt_estoque_p bigint, nr_seq_lote_fornec_p bigint, nr_seq_area_prep_p bigint) AS $body$
DECLARE

nr_sequencia_w bigint;


BEGIN

update 	gedipa_estoque_cabine
set 	qt_estoque = qt_estoque + qt_estoque_p,
		dt_atualizacao = clock_timestamp(),
		nm_usuario = nm_usuario_p
where 	cd_material = cd_material_p
and 	((nr_seq_lote_fornec = nr_seq_lote_fornec_p) or ((coalesce(nr_seq_lote_fornec::text, '') = '') and (coalesce(nr_seq_lote_fornec_p::text, '') = '')))
and 	nr_seq_area_prep = nr_seq_area_prep_p
and		qt_estoque > 0;
if (NOT FOUND) then
	select nextval('gedipa_estoque_cabine_seq')
	into STRICT nr_sequencia_w
	;

	insert into gedipa_estoque_cabine(
		nr_sequencia,
		dt_atualizacao,
		nm_usuario,
		dt_atualizacao_nrec,
		nm_usuario_nrec,
		cd_material,
		qt_estoque,
		nr_seq_lote_fornec,
		nr_seq_area_prep)
	values (nr_sequencia_w,
		clock_timestamp(),
		nm_usuario_p,
		clock_timestamp(),
		nm_usuario_p,
		cd_material_p,
		qt_estoque_p,
		nr_seq_lote_fornec_p,
		nr_seq_area_prep_p);
end if;

