CREATE OR REPLACE PROCEDURE gravar_gestao_vaga_email ( nr_seq_gv_p bigint, ds_destinatario_p text, ds_assunto_p text, nm_usuario_p text) AS $body$
DECLARE


nr_sequencia_w 		bigint;


BEGIN

select 	nextval('gestao_vaga_email_seq')
into STRICT	nr_sequencia_w
;

insert into gestao_vaga_email(	nr_sequencia,
		dt_atualizacao,
		nm_usuario,
		dt_atualizacao_nrec,
		nm_usuario_nrec,
		nr_seq_gestao_vaga,
		ds_email,
		ds_destinatario,
		ds_assunto)
SELECT
	nr_sequencia_w,
	clock_timestamp(),
	nm_usuario_p,
	clock_timestamp(),
	nm_usuario_p,
	nr_seq_gv_p,
	a.ds_conteudo,
	substr(ds_destinatario_p,1,255),
	ds_assunto_p
	from W_GV_ENVIO_EMAIL a
	where nr_sequencia = nr_seq_gv_p;
	
	
		
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
