CREATE OR REPLACE PROCEDURE gravar_historico_alta (nr_atendimento_p bigint, ie_alta_estorno_p text, dt_alta_estorno_p timestamp, cd_motivo_p bigint, ds_observacao_p text, nm_usuario_p text, dt_alta_real_p timestamp default null) AS $body$
DECLARE


nr_seq_historico_w		bigint;
nr_seq_motivo_est_alta_w	bigint;


BEGIN
if (nr_atendimento_p IS NOT NULL AND nr_atendimento_p::text <> '') and (ie_alta_estorno_p IS NOT NULL AND ie_alta_estorno_p::text <> '') and (dt_alta_estorno_p IS NOT NULL AND dt_alta_estorno_p::text <> '') then
	begin
	/* obter sequencia */

	select	nextval('atend_alta_hist_seq')
	into STRICT	nr_seq_historico_w
	;

	if (ie_alta_estorno_p = 'E') then

		select	max(nr_seq_motivo_est_alta)
		into STRICT	nr_seq_motivo_est_alta_w
		from	atend_paciente_auxiliar
		where	nr_sequencia in (SELECT	max(b.nr_sequencia)
					   from	atend_paciente_auxiliar b
					  where	b.nr_atendimento	= nr_atendimento_p);
	end if;
	/* gerar historico */

	insert into atend_alta_hist(
					nr_sequencia,
					nm_usuario_nrec,
					dt_atualizacao_nrec,
					nm_usuario,
					dt_atualizacao,
					nr_atendimento,
					ie_alta_estorno,
					dt_alta_estorno,
					cd_motivo_alta,
					ds_observacao,
					dt_alta,
					nr_seq_motivo_est_alta
					)
	values (
					nr_seq_historico_w,
					nm_usuario_p,
					clock_timestamp(),
					nm_usuario_p,
					clock_timestamp(),
					nr_atendimento_p,
					ie_alta_estorno_p,
					dt_alta_estorno_p,
					CASE WHEN ie_alta_estorno_p='A' THEN cd_motivo_p  ELSE null END ,
					ds_observacao_p,
					dt_alta_real_p,
					nr_seq_motivo_est_alta_w
					);

	CALL gravar_log_tasy(98823, wheb_mensagem_pck.get_texto(795194) || ' ' || wheb_mensagem_pck.get_texto(795190) || ': ' || nr_seq_historico_w ||
				' * ' || wheb_mensagem_pck.get_texto(307420) || ': ' || nr_atendimento_p ||
				' * ' || wheb_mensagem_pck.get_texto(795195) || ': ' || Obter_perfil_Ativo ||
				' * ' || wheb_mensagem_pck.get_texto(795196) || ': ' || Obter_Funcao_Ativa ||
				' * ' || wheb_mensagem_pck.get_texto(308466) || ': ' || nm_usuario_p, nm_usuario_p);


	end;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
