CREATE OR REPLACE PROCEDURE gravar_historico_identific_pac ( nr_atendimento_p bigint, nr_cirurgia_p bigint, ie_processo_p text, ds_historico_p text, nm_usuario_p text) AS $body$
DECLARE

/*
	valor do campo ie_processo_p
	IP - Inclusão painel
	IE - Impressão de etiqueta
	RE - reimpressão de etiqueta
	CA - checagem do paciente cirúrgico bipando o atendimento
	CP - checagem do paciente cirúrgico bipando a pulseira
	CC - checagem do paciente cirúrgico bipando a sala cirúrgica
*/
BEGIN

if (coalesce(nr_atendimento_p,0) > 0) then
	insert into atend_hist_checagem_pda(
			nr_sequencia,
			dt_atualizacao,
			nm_usuario,
			dt_atualizacao_nrec,
			nm_usuario_nrec,
			nr_atendimento,
			ie_processo,
			ds_historico,
			nr_cirurgia)
	SELECT	nextval('atend_hist_checagem_pda_seq'),
			clock_timestamp(),
			nm_usuario_p,
			clock_timestamp(),
			nm_usuario_p,
			nr_atendimento_p,
			ie_processo_p,
			ds_historico_p,
			CASE WHEN nr_cirurgia_p=0 THEN null  ELSE nr_cirurgia_p END
	;
end if;


commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
