CREATE OR REPLACE PROCEDURE gravar_historico_opm ( nr_seq_opm_p bigint, nm_usuario_p text, ds_justificativa_p text, ie_estagio_atual_p text, dt_inclusao_secretaria_p timestamp) AS $body$
DECLARE


nr_seq_op_opm_w		bigint;

BEGIN

insert into prescr_opm_historico(nr_sequencia,
		dt_atualizacao,
		nm_usuario,
		dt_atualizacao_nrec,
		nm_usuario_nrec,
		nr_seq_opm,
		ds_justificativa,
		ie_estagio_anterior,
		ie_estagio_atual,
		dt_inclusao)
values (nextval('prescr_opm_historico_seq'),
		clock_timestamp(),
		nm_usuario_p,
		clock_timestamp(),
		nm_usuario_p,
		nr_seq_opm_p,
		ds_justificativa_p,
		substr(obter_estagio_atual_opm(nr_seq_opm_p),1,255),
		ie_estagio_atual_p,
		dt_inclusao_secretaria_p);

update	prescr_opm
set		ie_estagio_atual 	= ie_estagio_atual_p
where		nr_sequencia		= nr_seq_opm_p;

if (ie_estagio_atual_p = 'OC') then
	select	max(nr_sequencia)
	into STRICT 	 	nr_seq_op_opm_w
	from 	 	ordem_producao_opm
	where  	nr_seq_opm = nr_seq_opm_p;

	update	ordem_producao_opm
	set		ie_situacao 				= 'I',
				nm_usuario_inativacao	= nm_usuario_p,
				dt_inativacao				= clock_timestamp()
	where		nr_seq_opm					= nr_seq_opm_p;

	CALL gravar_status_op_opm(nr_seq_op_opm_w,'COP',null,nm_usuario_p,null);
end if;

if (WHEB_USUARIO_PCK.get_ie_commit = 'S') then commit; end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
