CREATE OR REPLACE PROCEDURE gravar_historico_proc_interno (nr_seq_proc_interno_p bigint, ds_titulo_p text, ds_historico_p text, nm_usuario_p text) AS $body$
DECLARE


ds_historico_w				text;
nr_sequencia_w				bigint;


BEGIN

if (nr_seq_proc_interno_p IS NOT NULL AND nr_seq_proc_interno_p::text <> '') and (ds_titulo_p IS NOT NULL AND ds_titulo_p::text <> '') and (ds_historico_p IS NOT NULL AND ds_historico_p::text <> '') and (nm_usuario_p IS NOT NULL AND nm_usuario_p::text <> '') then

	begin
	ds_historico_w	:= ds_historico_p;

	select	nextval('proc_interno_hist_seq')
	into STRICT	nr_sequencia_w
	;

	insert into proc_interno_hist(
		nr_sequencia,
		dt_atualizacao,
		nm_usuario,
		dt_atualizacao_nrec,
		nm_usuario_nrec,
		ds_historico,
		nr_seq_proc_interno,
		ds_titulo)
	values (	nr_sequencia_w,
		clock_timestamp(),
		nm_usuario_p,
		clock_timestamp(),
		nm_usuario_p,
		substr(ds_historico_p,1,4000),
		nr_seq_proc_interno_p,
		ds_titulo_p);
	commit;

	end;

end if;

end;
$body$
