SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE gravar_horario_agenda_global (cd_agenda_p bigint, dt_agenda_p timestamp, hr_inicio_p timestamp, cd_pessoa_fisica_p text, nm_pessoa_fisica_p text, cd_medico_p text, nm_usuario_p text, cd_procedimento_p bigint, ie_origem_proced_p bigint, cd_convenio_p bigint, nr_seq_proc_interno_p bigint, cd_estabelecimento_p bigint, ds_observacao_p text, cd_categoria_p text, nr_doc_convenio_p text, ds_cirurgia_p text, cd_usuario_convenio_p text, dt_validade_carteira_p timestamp, ds_erro_p INOUT text) AS $body$
DECLARE


nr_seq_agenda_w		            agenda_paciente.nr_sequencia%type;
ie_status_agenda_w	varchar(01);
cd_medico_w		varchar(10);
cd_procedimento_w	bigint;
ie_origem_proced_w	bigint;
qt_idade_w		bigint;
dt_nascimento_w		timestamp;


BEGIN

select	coalesce(max(nr_sequencia), 0)
into STRICT	nr_seq_agenda_w
from	agenda_paciente
where	cd_agenda		= cd_agenda_p
and	dt_agenda		= dt_agenda_p
and	hr_inicio		= hr_inicio_p;

if (nr_seq_agenda_w > 0) then
	begin

	select	max(ie_status_agenda)
	into STRICT	ie_status_agenda_w
	from 	agenda_paciente
	where	nr_sequencia	= nr_seq_agenda_w;

	cd_procedimento_w	:= null;
	ie_origem_proced_w	:= null;

	if (nr_seq_proc_interno_p > 0) then
		begin

		SELECT * FROM Obter_Proc_Tab_Inter_Agenda(nr_seq_proc_interno_p, nr_seq_agenda_w, cd_convenio_p, null, null, cd_estabelecimento_p, dt_agenda_p, null, null, null, null, null, null, null, null, cd_procedimento_w, ie_origem_proced_w) INTO STRICT cd_procedimento_w, ie_origem_proced_w;

		end;
	end if;

	if (ie_status_agenda_w = 'L') then
		begin


		select 	max(obter_idade(dt_nascimento, clock_timestamp(), 'A')),
			max(dt_nascimento)
		into STRICT	qt_idade_w,
			dt_nascimento_w
                from 	pessoa_fisica
                where 	cd_pessoa_fisica = cd_pessoa_fisica_p;


		update	agenda_paciente
		set	ie_status_agenda	= 'N',
			cd_pessoa_fisica	= cd_pessoa_fisica_p,
			nm_paciente		= nm_pessoa_fisica_p,
			cd_medico		= cd_medico_p,
			nm_usuario		= nm_usuario_p,
			dt_atualizacao		= clock_timestamp(),
			cd_procedimento		= coalesce(cd_procedimento_w, cd_procedimento_p),
			ie_origem_proced	= coalesce(ie_origem_proced_w, ie_origem_proced_p),
			cd_convenio		= cd_convenio_p,
			DT_NASCIMENTO_PAc	= dt_nascimento_w,
			QT_IDADE_PACIENTE	= qt_idade_w,
			nr_seq_proc_interno	= CASE WHEN nr_seq_proc_interno_p=0 THEN  null  ELSE nr_seq_proc_interno_p END ,
			ds_observacao		= ds_observacao_p,
			DT_AGENDAMENTO		= clock_timestamp(),
			nm_usuario_orig		= nm_usuario_p,
			nr_telefone		= obter_fone_pac_agenda(cd_pessoa_fisica_p),
			cd_categoria 		= cd_categoria_p,
			nr_doc_convenio 	= nr_doc_convenio_p,
			ds_cirurgia 		= ds_cirurgia_p,
			dt_validade_carteira 	= dt_validade_carteira_p,
			cd_usuario_convenio	= cd_usuario_convenio_p
		where	nr_sequencia		= nr_seq_agenda_w;
			

		end;
	else
		ds_erro_p	:= WHEB_MENSAGEM_PCK.get_texto(278772);		
	end if;

	end;
else
	ds_erro_p	:= WHEB_MENSAGEM_PCK.get_texto(278773);
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
