CREATE OR REPLACE PROCEDURE gravar_integracao_cross_atx ( nr_seq_evento_p bigint, ds_parametros_p text) AS $body$
DECLARE


ie_integra_cross_w			varchar(1);
BEGIN
if (coalesce(nr_seq_evento_p,0) > 0) then

    select  coalesce(max(ie_integra_cross),'N')
	into STRICT	ie_integra_cross_w
	from 	parametro_atendimento
